use rand::Rng;
use std::f64::consts::PI;

struct Point {
    x: f64,
    y: f64,
}

fn main() {
    let mut rng = rand::thread_rng();
    let width = 800.0;
    let height = 600.0;
    let resolution = 5;

    println!("P5\n{} {}\n255", width, height);

    for y in (0..height).step_by(resolution) {
        for x in (0..width).step_by(resolution) {
            let angle = rng.gen_range(0.0..PI * 2.0);
            let radius = rng.gen_range(10.0..50.0);
            let point = Point { x, y };

            let dist = distance(point, center(width, height));
            if dist < radius {
                println!("{} {} {}", (point.x / resolution).round() as u32, (point.y / resolution).round() as u32, rng.gen_range(0..=255) as u8);
            }
        }
    }
}

fn distance(p1: Point, p2: Point) -> f64 {
    ((p1.x - p2.x).powi(2) + (p1.y - p2.y).powi(2)).sqrt()
}

fn center(width: f64, height: f64) -> Point {
    Point { x: width / 2.0, y: height / 2.0 }
}

using System;
using System.Collections.Generic;

class Book {
    public string Title { get; set; }
    public string Author { get; set; }
    public int YearPublished { get; set; }

    public Book(string title, string author, int year) {
        Title = title;
        Author = author;
        YearPublished = year;
    }
}

class BookshelfManager {
    private List<Book> books;

    public BookshelfManager() {
        books = new List<Book>();
    }

    public void AddBook(Book book) {
        books.Add(book);
        Console.WriteLine($"{book.Title} by {book.Author} added to the shelf.");
    }

    public void RemoveBook(string title) {
        Book bookToRemove = books.Find(b => b.Title == title);
        if (bookToRemove != null) {
            books.Remove(bookToRemove);
            Console.WriteLine($"Removed: {title}");
        } else {
            Console.WriteLine($"{title} not found on the shelf.");
        }
    }

    public void SearchBooks(string query) {
        List<Book> results = books.FindAll(b => b.Title.Contains(query) || b.Author.Contains(query));
        if (results.Count > 0) {
            Console.WriteLine("Search Results:");
            foreach (var book in results) {
                Console.WriteLine($"{book.Title} by {book.Author}, Published: {book.YearPublished}");
            }
        } else {
            Console.WriteLine("No books found matching the query.");
        }
    }

    public void DisplayAllBooks() {
        if (books.Count > 0) {
            Console.WriteLine("Current Books on Shelf:");
            foreach (var book in books) {
                Console.WriteLine($"{book.Title} by {book.Author}, Published: {book.YearPublished}");
            }
        } else {
            Console.WriteLine("No books on the shelf.");
        }
    }
}

class Program {
    static void Main(string[] args) {
        BookshelfManager manager = new BookshelfManager();
        while (true) {
            Console.WriteLine("\n1. Add a book");
            Console.WriteLine("2. Remove a book");
            Console.WriteLine("3. Search books");
            Console.WriteLine("4. Display all books");
            Console.WriteLine("5. Exit");
            Console.Write("Enter your choice: ");
            int choice = Convert.ToInt32(Console.ReadLine());

            switch (choice) {
                case 1:
                    Console.Write("Title: ");
                    string title = Console.ReadLine();
                    Console.Write("Author: ");
                    string author = Console.ReadLine();
                    Console.Write("Year Published: ");
                    int year = Convert.ToInt32(Console.ReadLine());
                    manager.AddBook(new Book(title, author, year));
                    break;
                case 2:
                    Console.Write("Title of the book to remove: ");
                    title = Console.ReadLine();
                    manager.RemoveBook(title);
                    break;
                case 3:
                    Console.Write("Search query (title or author): ");
                    string query = Console.ReadLine();
                    manager.SearchBooks(query);
                    break;
                case 4:
                    manager.DisplayAllBooks();
                    break;
                case 5:
                    return;
                default:
                    Console.WriteLine("Invalid choice. Please try again.");
                    break;
            }
        }
    }
}

