fn main() {
    let mut canvas = vec![vec![' '; 20]; 10];
    draw(canvas.as_mut_slice(), &[(5, 3), (6, 3), (7, 3)], 'X');
    print_canvas(&canvas);
}

fn draw(canvas: &mut Vec<Vec<char>>, points: &[(usize, usize)], color: char) {
    for &(x, y) in points.iter() {
        if let Some(row) = canvas.get_mut(y) {
            if let Some(cell) = row.get_mut(x) {
                *cell = color;
            }
        }
    }
}

fn print_canvas(canvas: &[Vec<char>]) {
    println!("ASCII Canvas:");
    for row in canvas {
        println!("{}", row.iter().collect::<String>());
    }
}

// Define a struct for the player character
struct Player {
    name: String,
    health: u32,
    strength: u32,
}

// Define a struct for an enemy
struct Enemy {
    name: String,
    health: u32,
    attack_power: u32,
}

// Function to simulate a battle between the player and an enemy
fn battle(player: &mut Player, enemy: &Enemy) {
    println!("{} attacks {}!", player.name, enemy.name);

    // Player attacks first
    enemy.health -= player.strength;
    if enemy.health <= 0 {
        println!("{} has been defeated!", enemy.name);
        return;
    }

    // Enemy then attacks the player
    player.health -= enemy.attack_power;
    if player.health <= 0 {
        println!("{} has been defeated!", player.name);
    } else {
        println!("Player's health: {}", player.health);
    }
}

fn main() {
    // Create a new player and an enemy
    let mut player = Player {
        name: String::from("Hero"),
        health: 100,
        strength: 20,
    };

    let enemy = Enemy {
        name: String::from("Goblin"),
        health: 50,
        attack_power: 15,
    };

    // Start the battle
    battle(&mut player, &enemy);
}

