int main() {
    // Load an image
    cv::Mat img = cv::imread("path_to_image.jpg");

    if (img.empty()) {
        std::cout << "Could not open or find the image" << std::endl;
        return -1;
    }

    // Define parameters for color grading
    double brightness = 50.0; 
    double contrast = 127.0; 
    double saturation = 1.5; 
    cv::Scalar hue = cv::Scalar(30, 0, 0); 

    // Adjust image properties
    img.convertTo(img, -1, contrast/127.0, brightness);
    cv::cvtColor(img, img, cv::COLOR_BGR2HSV);
    img += hue;
    cv::cvtColor(img, img, cv::COLOR_HSV2BGR);
    img.convertTo(img, -1, saturation);

    // Save or display the image
    cv::imwrite("path_to_output_image.jpg", img);
    // or
    // cv::imshow("Image", img);
    // cv::waitKey(0);

    return 0;
}

