use std::collections::HashMap;
use std::time::{Duration, Instant};

struct RusticMemo {
    data: HashMap<String, (String, Instant)>,
}

impl RusticMemo {
    fn new() -> Self {
        RusticMemo {
            data: HashMap::new(),
        }
    }

    fn set(&mut self, key: String, value: String, ttl: Option<Duration>) {
        let expiration = ttl.map(|d| Instant::now() + d);
        self.data.insert(key, (value, expiration));
    }

    fn get(&self, key: &str) -> Option<&String> {
        match self.data.get(key) {
            Some((val, exp)) if exp.is_none() || exp.unwrap().elapsed().as_secs() < 1 => Some(val),
            _ => None,
        }
    }

    fn delete(&mut self, key: &str) {
        self.data.remove(key);
    }
}

fn main() {
    let mut store = RusticMemo::new();
    store.set("name".to_string(), "Alice".to_string(), None);
    println!("Name: {:?}", store.get("name"));
    store.delete("name");
    println!("Name after delete: {:?}", store.get("name"));
}

import edu.stanford.nlp.ling.CoreAnnotations;
import edu.stanford.nlp.pipeline.*;
import edu.stanford.nlp.sentiment.SentimentCoreAnnotations;

public class Chatbot {
    private static final Properties props = new Properties();
    private static StanfordCoreNLP pipeline;

    static {
        props.setProperty("annotators", "tokenize, ssplit, pos, lemma, parse, sentiment");
        pipeline = new StanfordCoreNLP(props);
    }

    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        System.out.println("Chatbot: Hello! How can I assist you today?");

        while (true) {
            String input = scanner.nextLine();
            if ("exit".equalsIgnoreCase(input)) break;
            respondToUser(input);
        }
    }

    private static void respondToUser(String text) {
        // Create an empty Annotation just with the given text
        Annotation annotation = new Annotation(text);

        // Run all Annotators on this text
        pipeline.annotate(annotation);

        String sentiment = annotation.get(CoreAnnotations.SentimentAnnotatedTree.class).label().toString();

        switch (sentiment.toLowerCase()) {
            case "very negative":
                System.out.println("Chatbot: I'm sorry to hear that. Is there anything else I can help with?");
                break;
            case "negative":
                System.out.println("Chatbot: Could you please rephrase your question? I might not have understood correctly.");
                break;
            case "neutral":
                System.out.println("Chatbot: I understand. What can I do for you today?");
                break;
            case "positive":
                System.out.println("Chatbot: That's great! Is there anything else I can assist with?");
                break;
            case "very positive":
                System.out.println("Chatbot: That sounds wonderful! How can I help you further?");
                break;
        }
    }
}

using System;
using Microsoft.ML;
using Microsoft.ML.Data;

namespace MusicGenreClassification
{
    class Program
    {
        static void Main(string[] args)
        {
            // Initialize MLContext
            var mlContext = new MLContext();

            // Define data schema
            var dataSchema = mlContext.Schema.Create(
                typeof(float), "Acousticness",
                typeof(float), "Danceability",
                typeof(float), "Energy",
                typeof(float), "Liveness",
                typeof(float), "Valence",
                typeof(string), "Genre"
            );

            // Load the training data
            var trainData = mlContext.Data.LoadFromTextFile<MusicTrack>("music_training_data.csv", schema: dataSchema, hasHeader: true);

            // Define the pipeline
            var pipeline = mlContext.Transforms.Concatenate("Features", "Acousticness", "Danceability", "Energy", "Liveness", "Valence")
                .Append(mlContext.Transforms.NormalizeMinMax("Features"))
                .Append(mlContext.MulticlassClassification.Trainers.SdcaNonCalibrated())
                .Append(mlContext.Transforms.Conversion.MapValueToKey("PredictedGenre"));

            // Train the model
            var model = pipeline.Fit(trainData);

            // Load the test data
            var testData = mlContext.Data.LoadFromTextFile<MusicTrack>("music_test_data.csv", schema: dataSchema, hasHeader: true);

            // Evaluate the model
            var predictions = model.Transform(testData);
            var metrics = mlContext.MulticlassClassification.Evaluate(predictions);

            Console.WriteLine($"Accuracy: {metrics.MacroAverageAccuracy}");
        }
    }

    [LoadColumn(0)]
    public float Acousticness;

    [LoadColumn(1)]
    public float Danceability;

    [LoadColumn(2)]
    public float Energy;

    [LoadColumn(3)]
    public float Liveness;

    [LoadColumn(4)]
    public float Valence;

    [LoadColumn(5)]
    public string Genre;
}

