using System;
using System.Collections.Generic;

class Room {
    public string Description { get; set; }
    public Dictionary<string, Room> Exits { get; set; }

    public Room(string description) {
        Description = description;
        Exits = new Dictionary<string, Room>();
    }

    public void AddExit(string direction, Room room) {
        Exits[direction] = room;
    }
}

class Game {
    private Room currentRoom;

    public Game() {
        // Create rooms
        Room bedroom = new Room("You are in a dark bedroom. There is a door to the north.");
        Room kitchen = new Room("You are in a dimly lit kitchen. The smell of cooking fills the air. There is a door to the south.");

        // Add exits
        bedroom.AddExit("north", kitchen);
        kitchen.AddExit("south", bedroom);

        // Set starting room
        currentRoom = bedroom;
    }

    public void Play() {
        Console.WriteLine(currentRoom.Description);

        while (true) {
            string input = Console.ReadLine().ToLower();
            if (input == "quit") break;

            string[] words = input.Split(' ');
            string command = words[0];
            string direction = words.Length > 1 ? words[1] : "";

            switch (command) {
                case "go":
                    if (currentRoom.Exits.ContainsKey(direction)) {
                        currentRoom = currentRoom.Exits[direction];
                        Console.WriteLine(currentRoom.Description);
                    } else {
                        Console.WriteLine("You can't go that way.");
                    }
                    break;
                default:
                    Console.WriteLine("I don't understand that command.");
                    break;
            }
        }
    }
}

class Program {
    static void Main(string[] args) {
        Game game = new Game();
        game.Play();
    }
}

