import java.awt.image.BufferedImage;
import javax.imageio.ImageIO;
import com.google.cloud.vision.v1.AnnotateImageRequest;
import com.google.cloud.vision.v1.AnnotateImageResponse;
import com.google.cloud.vision.v1.EntityAnnotation;
import com.google.cloud.vision.v1.Feature;
import com.google.cloud.vision.v1.Image;
import com.google.cloud.vision.v1.ImageAnnotatorClient;

public class ImageTextExtractor {
    public static void main(String[] args) {
        String imagePath = "path/to/your/image.jpg";
        try {
            BufferedImage bufferedImage = ImageIO.read(new File(imagePath));
            byte[] imageBytes = toByteArray(bufferedImage);

            Image image = Image.newBuilder().setContent(ByteString.copyFrom(imageBytes)).build();
            Feature feature = Feature.newBuilder().setType(Feature.Type.TEXT_DETECTION).build();

            AnnotateImageRequest request = AnnotateImageRequest.newBuilder()
                    .addFeatures(feature)
                    .setImage(image)
                    .build();

            try (ImageAnnotatorClient client = ImageAnnotatorClient.create()) {
                AnnotateImageResponse response = client.annotateImage(request);
                for (EntityAnnotation annotation : response.getTextAnnotationsList()) {
                    System.out.println("Detected text: " + annotation.getDescription());
                }
            }
        } catch (Exception e) {
            e.printStackTrace();
        }
    }

    private static byte[] toByteArray(BufferedImage image) throws IOException {
        ByteArrayOutputStream bos = new ByteArrayOutputStream();
        ImageIO.write(image, "jpg", bos);
        return bos.toByteArray();
    }
}

