import (
	"fmt"
	"image/jpeg"
	"os"
	"strings"

	"github.com/disintegration/imaging"
)

func resizeImage(inputPath string, outputPath string, newWidth int, newHeight int) error {
	file, err := os.Open(inputPath)
	if err != nil {
		return fmt.Errorf("failed to open file: %w", err)
	}
	defer file.Close()

	img, err := jpeg.Decode(file)
	if err != nil {
		return fmt.Errorf("failed to decode image: %w", err)
	}

	resizedImg := imaging.Resize(img, newWidth, newHeight, imaging.Lanczos3)

	outputFile, err := os.Create(outputPath)
	if err != nil {
		return fmt.Errorf("failed to create output file: %w", err)
	}
	defer outputFile.Close()

	err = jpeg.Encode(outputFile, resizedImg, &jpeg.Options{Quality: 95})
	if err != nil {
		return fmt.Errorf("failed to encode image: %w", err)
	}

	return nil
}

func main() {
	inputPath := "input.jpg"
	outputPath := "output.jpg"
	newWidth := 800
	newHeight := 600

	err := resizeImage(inputPath, outputPath, newWidth, newHeight)
	if err != nil {
		fmt.Println("Error:", err)
		return
	}

	fmt.Println("Image resized and saved successfully!")
}

import java.awt.Color;
import java.awt.Graphics;
import javax.sound.sampled.*;
import javax.swing.JFrame;

public class MusicVisualizer extends JFrame implements AudioListener {
    private int[] frequencies = new int[256];
    private Clip audioClip;

    public MusicVisualizer() {
        setSize(800, 400);
        setDefaultCloseOperation(JFrame.EXIT_ON_CLOSE);
        addAudioListener(this);
        loadAudio("path/to/your/audio/file.wav");
    }

    @Override
    public void update(Graphics g) {
        Graphics2D g2d = (Graphics2D) g;
        g2d.setColor(Color.BLACK);
        g2d.fillRect(0, 0, getWidth(), getHeight());

        for (int i = 0; i < frequencies.length; i++) {
            int x = i * 3;
            int height = frequencies[i] / 4;
            g2d.setColor(new Color(i % 256, 255 - i % 256, 128));
            g2d.fillRect(x, getHeight() - height, 2, height);
        }
    }

    @Override
    public void audioUpdate(AudioEvent event) {
        AudioFormat format = event.getAudioFormat();
        byte[] buffer = event.getBuffer();
        int length = event.getLength();

        for (int i = 0; i < length; i += 4) {
            short leftSample = (short)((buffer[i] << 8) | (buffer[i+1] & 0xFF));
            short rightSample = (short)((buffer[i+2] << 8) | (buffer[i+3] & 0xFF));

            int sampleValue = Math.abs(leftSample + rightSample);
            frequencies[i / 4] = sampleValue;
        }
    }

    private void loadAudio(String filePath) {
        try {
            AudioInputStream audioStream = AudioSystem.getAudioInputStream(new File(filePath));
            AudioFormat format = audioStream.getFormat();
            DataLine.Info info = new DataLine.Info(Clip.class, format);

            audioClip = (Clip) AudioSystem.getLine(info);
            audioClip.open(audioStream);
            audioClip.start();
        } catch (Exception e) {
            e.printStackTrace();
        }
    }

    public static void main(String[] args) {
        MusicVisualizer visualizer = new MusicVisualizer();
        visualizer.setVisible(true);
    }
}

