import java.util.Random;
import java.util.Scanner;

public class TextBasedHangman {
    private static final String[] WORDS = {"JAVA", "PROGRAMMING", "ALGORITHM", "DATASTRUCTURE"};
    private static final int MAX_ATTEMPTS = 6;

    public static void main(String[] args) {
        Random random = new Random();
        String wordToGuess = WORDS[random.nextInt(WORDS.length)].toLowerCase();
        StringBuilder guessedWord = new StringBuilder(wordToGuess.length());
        for (int i = 0; i < wordToGuess.length(); i++) {
            guessedWord.append("_");
        }
        int attemptsLeft = MAX_ATTEMPTS;
        Set<Character> guessedLetters = new HashSet<>();

        Scanner scanner = new Scanner(System.in);
        while (!guessedWord.toString().equals(wordToGuess) && attemptsLeft > 0) {
            System.out.println("Current Word: " + guessedWord);
            System.out.print("Attempts Left: " + attemptsLeft);
            System.out.print("\nEnter a letter: ");
            String input = scanner.nextLine();
            if (input.length() != 1 || !Character.isLetter(input.charAt(0))) {
                System.out.println("Please enter a single letter.");
                continue;
            }
            char guessedLetter = Character.toLowerCase(input.charAt(0));
            if (!guessedLetters.contains(guessedLetter)) {
                guessedLetters.add(guessedLetter);
                if (wordToGuess.indexOf(guessedLetter) != -1) {
                    for (int i = 0; i < wordToGuess.length(); i++) {
                        if (wordToGuess.charAt(i) == guessedLetter) {
                            guessedWord.setCharAt(i, guessedLetter);
                        }
                    }
                } else {
                    attemptsLeft--;
                }
            } else {
                System.out.println("You already guessed that letter.");
            }
        }

        if (guessedWord.toString().equals(wordToGuess)) {
            System.out.println("Congratulations! You guessed the word: " + wordToGuess);
        } else {
            System.out.println("Game Over! The correct word was: " + wordToGuess);
        }
    }
}

#include <iostream>
#include <vector>

class Image {
public:
    Image(int width, int height) : width(width), height(height) {
        pixels.resize(width * height);
    }

    void setPixel(int x, int y, unsigned char r, unsigned char g, unsigned char b) {
        if (x >= 0 && x < width && y >= 0 && y < height) {
            int index = y * width + x;
            pixels[index] = {r, g, b};
        }
    }

    void resize(int newWidth, int newHeight) {
        std::vector<std::array<unsigned char, 3>> newPixels(newWidth * newHeight);
        // Simple nearest neighbor resizing
        for (int y = 0; y < newHeight; ++y) {
            for (int x = 0; x < newWidth; ++x) {
                int oldX = static_cast<int>(x / static_cast<double>(newWidth) * width);
                int oldY = static_cast<int>(y / static_cast<double>(newHeight) * height);
                int index = y * newWidth + x;
                newPixels[index] = pixels[oldY * width + oldX];
            }
        }
        pixels = std::move(newPixels);
        width = newWidth;
        height = newHeight;
    }

private:
    struct Pixel {
        unsigned char r, g, b;
    };
    int width, height;
    std::vector<Pixel> pixels;
};

int main() {
    Image img(100, 100);
    img.setPixel(50, 50, 255, 0, 0); // Set a red pixel at the center

    img.resize(50, 50);

    return 0;
}

import java.awt.*;
import java.awt.event.*;
import javax.swing.*;

public class PixelArtCreator extends JPanel implements MouseListener {
    private static final int GRID_SIZE = 16;
    private static final int PANEL_WIDTH = 320;
    private static final int PANEL_HEIGHT = 320;
    private Color[][] pixels;

    public PixelArtCreator() {
        pixels = new Color[GRID_SIZE][GRID_SIZE];
        for (int i = 0; i < GRID_SIZE; i++) {
            for (int j = 0; j < GRID_SIZE; j++) {
                pixels[i][j] = Color.WHITE;
            }
        }
        setBackground(Color.BLACK);
        setPreferredSize(new Dimension(PANEL_WIDTH, PANEL_HEIGHT));
        addMouseListener(this);
    }

    @Override
    protected void paintComponent(Graphics g) {
        super.paintComponent(g);
        for (int i = 0; i < GRID_SIZE; i++) {
            for (int j = 0; j < GRID_SIZE; j++) {
                g.setColor(pixels[i][j]);
                g.fillRect(i * (PANEL_WIDTH / GRID_SIZE), j * (PANEL_HEIGHT / GRID_SIZE), PANEL_WIDTH / GRID_SIZE, PANEL_HEIGHT / GRID_SIZE);
            }
        }
    }

    @Override
    public void mouseClicked(MouseEvent e) {
        int x = e.getX() / (PANEL_WIDTH / GRID_SIZE);
        int y = e.getY() / (PANEL_HEIGHT / GRID_SIZE);
        pixels[x][y] = Color.BLACK; // Change color as needed, e.g., to allow for color selection
        repaint();
    }

    @Override
    public void mousePressed(MouseEvent e) {}

    @Override
    public void mouseReleased(MouseEvent e) {}

    @Override
    public void mouseEntered(MouseEvent e) {}

    @Override
    public void mouseExited(MouseEvent e) {}

    public static void main(String[] args) {
        JFrame frame = new JFrame("Pixel Art Creator");
        frame.setDefaultCloseOperation(JFrame.EXIT_ON_CLOSE);
        frame.add(new PixelArtCreator());
        frame.pack();
        frame.setLocationRelativeTo(null);
        frame.setVisible(true);
    }
}

