using System;
using System.Net.Http;
using Newtonsoft.Json;

class WeatherApp
{
    static async Task Main(string[] args)
    {
        Console.WriteLine("Welcome to the Weather Forecast Application!");
        Console.Write("Enter city name: ");
        string cityName = Console.ReadLine();

        try
        {
            string apiKey = "your_api_key_here";
            string url = $"https://api.openweathermap.org/data/2.5/weather?q={cityName}&appid={apiKey}&units=metric";

            using (HttpClient client = new HttpClient())
            {
                HttpResponseMessage response = await client.GetAsync(url);
                if (response.IsSuccessStatusCode)
                {
                    string jsonResponse = await response.Content.ReadAsStringAsync();
                    WeatherData weatherData = JsonConvert.DeserializeObject<WeatherData>(jsonResponse);

                    Console.WriteLine($"Current temperature in {weatherData.Name}: {weatherData.Main.Temp}°C");
                    Console.WriteLine($"Weather: {weatherData.Weather[0].Description}");
                    Console.WriteLine($"Wind Speed: {weatherData.Wind.Speed} m/s");
                }
                else
                {
                    Console.WriteLine("Error fetching weather data.");
                }
            }
        }
        catch (Exception ex)
        {
            Console.WriteLine($"An error occurred: {ex.Message}");
        }

        Console.WriteLine("Press any key to exit...");
        Console.ReadKey();
    }
}

