#include <iostream>
#include <string>
using namespace std;

class Room {
public:
    string description;
    bool hasItem;

    Room(string desc) : description(desc), hasItem(false) {}
};

int main() {
    // Create rooms
    Room room1("You find yourself in a dark forest. There is a path to the north and east.");
    Room room2("The path leads you to a small village. You can see a house to the south and an old tree to the west.");

    // Game loop
    bool gameOver = false;
    while (!gameOver) {
        int choice;
        cout << "What do you want to do?" << endl;
        cout << "1 - Go North" << endl;
        cout << "2 - Go East" << endl;
        cout << "3 - Exit Game" << endl;
        cin >> choice;

        switch (choice) {
            case 1:
                if (room1.description == "You find yourself in a dark forest. There is a path to the north and east.") {
                    room1.description = "You are now in a small clearing with a stream flowing through it. To the south is the forest.";
                    cout << room1.description << endl;
                } else if (room2.description == "The path leads you to a small village. You can see a house to the south and an old tree to the west.") {
                    cout << "You have already been in this room." << endl;
                }
                break;
            case 2:
                if (room1.description == "You find yourself in a dark forest. There is a path to the north and east.") {
                    cout << "You have already been in this room." << endl;
                } else if (room2.description == "The path leads you to a small village. You can see a house to the south and an old tree to the west.") {
                    room2.description = "You are now inside a cozy house with warm lighting. A wooden floor and comfortable furniture make it inviting.";
                    cout << room2.description << endl;
                }
                break;
            case 3:
                gameOver = true;
                cout << "Thank you for playing!" << endl;
                break;
            default:
                cout << "Invalid choice. Please try again." << endl;
        }
    }

    return 0;
}

