use std::io;

fn main() {
    let mut width = 0;
    let mut height = 0;

    println!("Welcome to PixelArt Editor!");
    loop {
        println!("\nEnter the dimensions of your pixel art (width x height):");
        let input = io::read_line(&mut io::stdin())
            .expect("Failed to read line")
            .trim()
            .to_string();

        if let Some((w, h)) = parse_dimensions(&input) {
            width = w;
            height = h;
            break;
        } else {
            println!("Invalid input. Please enter dimensions in the format 'width x height'.");
        }
    }

    let mut art = vec![vec![' '; width]; height];
    while true {
        print_art(&art);
        println!("\nEnter your command (e.g., draw 5,3 #):");
        let input = io::read_line(&mut io::stdin())
            .expect("Failed to read line")
            .trim()
            .to_string();

        if let Some((cmd, args)) = parse_command(&input) {
            match cmd {
                "draw" => draw_art(&mut art, args),
                "save" => save_art(&art),
                "exit" => break,
                _ => println!("Unknown command."),
            }
        } else {
            println!("Invalid input. Please enter a valid command.");
        }
    }
}

fn parse_dimensions(input: &str) -> Option<(usize, usize)> {
    let parts: Vec<&str> = input.split_whitespace().collect();
    if parts.len() == 2 {
        if let (Ok(w), Ok(h)) = (parts[0].parse(), parts[1].parse()) {
            return Some((w, h));
        }
    }
    None
}

fn parse_command(input: &str) -> Option<(&str, Vec<&str>)> {
    let parts: Vec<&str> = input.split_whitespace().collect();
    if !parts.is_empty() {
        return Some((parts[0], parts.iter().skip(1).map(|&s| s.trim()).collect()));
    }
    None
}

fn draw_art(art: &mut Vec<Vec<char>>, args: Vec<&str>) {
    if args.len() != 3 {
        println!("Invalid draw command. Please provide coordinates and a character.");
        return;
    }

    let (x, y) = match parse_coordinates(args[0]) {
        Some(coords) => coords,
        None => {
            println!("Invalid coordinates.");
            return;
        }
    };

    if x >= art[0].len() || y >= art.len() {
        println!("Coordinates out of bounds.");
        return;
    }

    let ch = args[2];
    if ch.len() != 1 {
        println!("Please provide a single character for drawing.");
        return;
    }

    art[y][x] = ch.chars().next().unwrap();
}

fn parse_coordinates(coord: &str) -> Option<(usize, usize)> {
    let parts: Vec<&str> = coord.split(',').collect();
