mod rustweb {
    use std::net::{TcpListener, TcpStream};
    use std::io::{Read, Write};

    pub struct Router {
        routes: Vec<(String, fn(TcpStream))>,
    }

    impl Router {
        pub fn new() -> Self {
            Router { routes: vec![] }
        }

        pub fn route(&mut self, path: &str, handler: fn(TcpStream)) {
            self.routes.push((path.to_string(), handler));
        }

        pub fn handle_request(&self, stream: TcpStream) {
            let mut buffer = [0; 512];
            stream.read(&mut buffer).unwrap();
            let request = String::from_utf8_lossy(&buffer);
            if let Some(route) = self.routes.iter().find(|(p, _)| p == &request.trim_end()) {
                (route.1)(stream);
            } else {
                let response = "HTTP/1.1 404 Not Found\r\n\r\n";
                stream.write(response.as_bytes()).unwrap();
                stream.flush().unwrap();
            }
        }
    }

    pub fn start_server(address: &str, router: Router) -> std::io::Result<()> {
        let listener = TcpListener::bind(address)?;
        println!("Server listening on {}", address);
        for stream in listener.incoming() {
            match stream {
                Ok(stream) => {
                    router.handle_request(stream);
                }
                Err(e) => {
                    eprintln!("Failed to establish a connection: {}", e);
                }
            }
        }
        Ok(())
    }

    pub fn hello_world(stream: TcpStream) {
        let response = "HTTP/1.1 200 OK\r\nContent-Type: text/plain\r\n\r\nHello, World!";
        stream.write(response.as_bytes()).unwrap();
        stream.flush().unwrap();
    }
}

fn main() {
    let mut router = rustweb::Router::new();
    router.route("/", rustweb::hello_world);
    rustweb::start_server("127.0.0.1:8080", router).unwrap();
}

