use std::fs;
use std::io::{self, Write};
use std::path::Path;

struct Note {
    title: String,
    content: String,
}

impl Note {
    fn new(title: &str) -> Self {
        Note {
            title: title.to_string(),
            content: String::new(),
        }
    }

    fn save(&self, path: &Path) -> io::Result<()> {
        let mut file = fs::File::create(path)?;
        write!(file, "# {}\n{}", self.title, self.content)
    }

    fn load(path: &Path) -> io::Result<Self> {
        let content = fs::read_to_string(path)?;
        let lines: Vec<&str> = content.lines().collect();
        if lines.is_empty() {
            return Err(io::Error::new(io::ErrorKind::InvalidData, "Empty note file"));
        }
        let title = lines[0].trim_start_matches('#').trim().to_string();
        let content = lines[1..].join("\n").to_string();
        Ok(Note { title, content })
    }

    fn add_content(&mut self, text: &str) {
        self.content.push_str(text);
    }
}

fn main() -> io::Result<()> {
    let args: Vec<String> = std::env::args().collect();
    if args.len() < 3 {
        eprintln!("Usage: {} [create|edit] filename", args[0]);
        return Ok(());
    }

    let action = &args[1];
    let filename = &args[2];
    let path = Path::new(filename);

    match action.as_str() {
        "create" => {
            let mut note = Note::new("New Note");
            println!("Edit your note. Type 'save' to save and exit.");
            io::stdin().lock().lines().take_while(|line| {
                let line = line.unwrap();
                if line.trim() == "save" {
                    false
                } else {
                    note.add_content(&format!("\n{}", line));
                    true
                }
            });
            note.save(path)?;
            println!("Note created and saved as {}", filename);
        },
        "edit" => {
            match Note::load(path) {
                Ok(mut note) => {
                    println!("Current content:\n{}\n", note.content);
                    println!("Edit your note. Type 'save' to save and exit.");
                    io::stdin().lock().lines().take_while(|line| {
                        let line = line.unwrap();
                        if line.trim() == "save" {
                            false
                        } else {
                            note.add_content(&format!("\n{}", line));
                            true
                        }
                    });
                    note.save(path)?;
                    println!("Note updated and saved as {}", filename);
                },
                Err(_) => println!("Could not load note. File might not exist."),
            }
        },
        _ => eprintln!("Unknown action: {}", action),
    }

    Ok(())
}

#include <iostream>
