// Define constants
const int WIDTH = 10;
const int HEIGHT = 10;
const int DEPTH = 10;

// Function to initialize a random 3D maze
void initMaze(std::vector<std::vector<std::vector<int>>>& maze) {
    for (int x = 0; x < WIDTH; ++x) {
        for (int y = 0; y < HEIGHT; ++y) {
            for (int z = 0; z < DEPTH; ++z) {
                maze[x][y][z] = rand() % 2; // Randomly place walls or paths
            }
        }
    }
}

// Function to display the maze
void displayMaze(const std::vector<std::vector<std::vector<int>>>& maze, int x, int y, int z) {
    for (int i = 0; i < DEPTH; ++i) {
        std::cout << "Layer " << i << ":" << std::endl;
        for (int j = 0; j < HEIGHT; ++j) {
            for (int k = 0; k < WIDTH; ++k) {
                if (x == k && y == j && z == i)
                    std::cout << "P "; // Player's position
                else if (maze[k][j][i] == 1)
                    std::cout << "# "; // Wall
                else
                    std::cout << ". "; // Path
            }
            std::cout << std::endl;
        }
        std::cout << std::endl;
    }
}

// Main function to run the maze solver
int main() {
    srand(time(0)); // Seed for random number generator

    std::vector<std::vector<std::vector<int>>> maze(WIDTH, std::vector<std::vector<int>>(HEIGHT, std::vector<int>(DEPTH)));

    initMaze(maze);
    int x = WIDTH / 2, y = HEIGHT / 2, z = DEPTH / 2;

    while (true) {
        displayMaze(maze, x, y, z);
        char move;
        std::cout << "Enter move (f/b/l/r/u/d): ";
        std::cin >> move;

        switch (move) {
            case 'f':
                if (x > 0 && maze[x-1][y][z] == 0)
                    --x;
                break;
            case 'b':
                if (x < WIDTH - 1 && maze[x+1][y][z] == 0)
                    ++x;
                break;
            case 'l':
                if (y > 0 && maze[x][y-1][z] == 0)
                    --y;
                break;
            case 'r':
                if (y < HEIGHT - 1 && maze[x][y+1][z] == 0)
                    ++y;
                break;
            case 'u':
                if (z > 0 && maze[x][y][z-1] == 0)
                    --z;
                break;
            case 'd':
                if (z < DEPTH - 1 && maze[x][y][z+1] == 0)
                    ++z;
                break;
            default:
                std::cout << "Invalid move!" << std::endl;
        }
    }

    return 0;
}

