#include <iostream>
#include <complex>
#include <cmath>

using namespace std;

// Function to calculate escape time for a given complex number
int mandelbrotEscapeTime(complex<double> c, int maxIterations) {
    complex<double> z = 0.0;
    int iteration = 0;
    while (iteration < maxIterations && abs(z) <= 2.0) {
        z = z * z + c;
        iteration++;
    }
    return iteration;
}

// Function to convert a pixel value to ASCII character
char pixelToASCII(int iterations, int maxIterations) {
    string asciiChars = "@%#*+=-:. ";
    float scale = 1.0f / (maxIterations - 1);
    int index = static_cast<int>(iterations * scale * (asciiChars.length() - 1));
    return asciiChars[index];
}

int main() {
    const int width = 80;
    const int height = 24;
    const complex<double> center(-0.75, 0.0);
    const double zoom = 2.0;
    const int maxIterations = 100;

    for (int y = 0; y < height; ++y) {
        for (int x = 0; x < width; ++x) {
            complex<double> c((x - width / 2.0) * zoom / width + center.real(),
                              (y - height / 2.0) * zoom / height + center.imag());
            int iterations = mandelbrotEscapeTime(c, maxIterations);
            cout << pixelToASCII(iterations, maxIterations);
        }
        cout << endl;
    }

    return 0;
}

