import java.util.Scanner;
import java.util.HashMap;

public class TextualAdventures {
    private static HashMap<String, Room> rooms = new HashMap<>();
    private static Room currentRoom;

    public static void main(String[] args) {
        // Initialize game rooms and connections
        initRooms();

        Scanner scanner = new Scanner(System.in);
        String command;

        while (true) {
            System.out.println("Current location: " + currentRoom.getDescription());
            System.out.print("What do you want to do? ");
            command = scanner.nextLine().toLowerCase();

            if ("quit".equals(command)) {
                break;
            } else {
                handleCommand(command);
            }
        }

        scanner.close();
    }

    private static void initRooms() {
        Room kitchen = new Room("Kitchen", "A dimly lit room with a table and a few chairs.");
        Room bedroom = new Room("Bedroom", "A cozy space with a bed, a nightstand, and a small window.");
        Room livingRoom = new Room("Living Room", "A bright room with a couch, a TV, and some books.");

        kitchen.addExit("north", livingRoom);
        bedroom.addExit("east", livingRoom);
        livingRoom.addExit("south", kitchen);
        livingRoom.addExit("west", bedroom);

        rooms.put(kitchen.getName(), kitchen);
        rooms.put(bedroom.getName(), bedroom);
        rooms.put(livingRoom.getName(), livingRoom);

        currentRoom = kitchen;
    }

    private static void handleCommand(String command) {
        String[] parts = command.split(" ");
        String verb = parts[0];
        String noun = (parts.length > 1) ? parts[1] : null;

        switch (verb) {
            case "go":
                if (noun != null && currentRoom.hasExit(noun)) {
                    currentRoom = rooms.get(currentRoom.getExit(noun).getName());
                } else {
                    System.out.println("You can't go that way.");
                }
                break;
            default:
                System.out.println("Unknown command: " + verb);
        }
    }
}

class Room {
    private String name;
    private String description;
    private HashMap<String, Room> exits = new HashMap<>();

    public Room(String name, String description) {
        this.name = name;
        this.description = description;
    }

    public void addExit(String direction, Room room) {
        exits.put(direction, room);
    }

    public boolean hasExit(String direction) {
        return exits.containsKey(direction);
    }

    public String getName() {
        return name;
    }

    public String getDescription() {
        return description;
    }

    public Room getExit(String direction) {
        return exits.get(direction);
    }
}

