#include <iostream>
using namespace std;

const int MAX_SIZE = 100;
int heap[MAX_SIZE];
bool allocated[MAX_SIZE];

void initializeHeap() {
    for(int i = 0; i < MAX_SIZE; ++i) {
        heap[i] = -1;
        allocated[i] = false;
    }
}

void allocateMemory(int size, int &index) {
    int start = -1;
    int count = 0;

    for(int i = 0; i < MAX_SIZE; ++i) {
        if(!allocated[i]) {
            if(start == -1) {
                start = i;
            }
            count++;
            if(count >= size) {
                index = start;
                return;
            }
        } else {
            start = -1;
            count = 0;
        }
    }

    cout << "Not enough contiguous memory available." << endl;
}

void freeMemory(int index, int size) {
    for(int i = index; i < index + size; ++i) {
        allocated[i] = false;
    }
}

void displayHeap() {
    for(int i = 0; i < MAX_SIZE; ++i) {
        if(allocated[i]) {
            cout << "A"; // Allocated block
        } else {
            cout << "-"; // Free space
        }
    }
    cout << endl;
}

int main() {
    initializeHeap();

    int choice, size, index;

    while(true) {
        cout << "\n1. Allocate Memory\n2. Free Memory\n3. Display Heap\n4. Exit\n";
        cout << "Enter your choice: ";
        cin >> choice;

        if(choice == 1) {
            cout << "Enter the size of memory to allocate: ";
            cin >> size;
            allocateMemory(size, index);
            if(index != -1) {
                for(int i = index; i < index + size; ++i) {
                    heap[i] = i;
                    allocated[i] = true;
                }
                cout << "Memory allocated successfully." << endl;
            }
        } else if(choice == 2) {
            cout << "Enter the starting index of memory to free: ";
            cin >> index;
            cout << "Enter the size of memory to free: ";
            cin >> size;
            freeMemory(index, size);
            cout << "Memory freed successfully." << endl;
        } else if(choice == 3) {
            displayHeap();
        } else if(choice == 4) {
            break;
        } else {
            cout << "Invalid choice. Please try again." << endl;
        }
    }

    return 0;
}

using System;
using System.Collections.Generic;
using System.Drawing;

class ImageColorPaletteGenerator
{
    static void Main(string[] args)
    {
        string imagePath = "path_to_image.jpg";
        List<Color> palette = GenerateColorPalette(imagePath, 5); // Get a list of 5 most prominent colors

        Console.WriteLine("Top 5 Color Palette:");
        foreach (var color in palette)
        {
            Console.WriteLine($"R: {color.R}, G: {color.G}, B: {color.B}");
        }
    }

    static List<Color> GenerateColorPalette(string imagePath, int numberOfColors)
    {
        using (Bitmap bitmap = new Bitmap(imagePath))
        {
            Dictionary<Color, int> colorCounts = new Dictionary<Color, int>();

            for (int y = 0; y < bitmap.Height; y++)
            {
                for (int x = 0; x < bitmap.Width; x++)
                {
                    Color pixelColor = bitmap.GetPixel(x, y);
                    if (!colorCounts.ContainsKey(pixelColor))
                    {
                        colorCounts[pixelColor] = 0;
                    }
                    colorCounts[pixelColor]++;
                }
            }

            List<Color> sortedColors = new List<KeyValuePair<Color, int>>(colorCounts);
            sortedColors.Sort((pair1, pair2) => pair2.Value.CompareTo(pair1.Value));

            return sortedColors.GetRange(0, Math.Min(numberOfColors, sortedColors.Count)).ConvertAll(pair => pair.Key);
        }
    }
}

