use std::fs::{File, OpenOptions};
use std::io::{Read, Write};
use std::path::Path;

struct Note {
    title: String,
    content: String,
}

impl Note {
    fn new(title: String, content: String) -> Self {
        Note { title, content }
    }

    fn save(&self, path: &Path) -> std::io::Result<()> {
        let mut file = OpenOptions::new().write(true).create(true).truncate(true).open(path)?;
        writeln!(file, "{}", self.title)?;
        writeln!(file, "{}", self.content)?;
        Ok(())
    }

    fn load(path: &Path) -> std::io::Result<Note> {
        let mut file = File::open(path)?;
        let mut title = String::new();
        file.read_to_string(&mut title)?;

        let mut content = String::new();
        file.read_to_string(&mut content)?;

        Ok(Note { 
            title: title.trim().to_string(),
            content: content.trim().to_string(),
        })
    }
}

fn main() -> std::io::Result<()> {
    let path = Path::new("note.txt");

    // Example usage
    let note = Note::new(String::from("My First Note"), String::from("This is the content of my first note."));
    note.save(&path)?;

    let loaded_note = Note::load(&path)?;
    println!("Title: {}", loaded_note.title);
    println!("Content: {}", loaded_note.content);

    Ok(())
}

