public class ImageTextOverlay extends JPanel {
    BufferedImage image;
    String text = "Sample Text";

    public ImageTextOverlay(String imagePath) throws IOException {
        this.image = ImageIO.read(new File(imagePath));
    }

    @Override
    protected void paintComponent(Graphics g) {
        super.paintComponent(g);
        Graphics2D g2d = (Graphics2D) g.create();
        g2d.drawImage(image, 0, 0, null);

        // Set text properties
        Font font = new Font("Arial", Font.BOLD, 48);
        g2d.setFont(font);
        g2d.setColor(Color.RED);

        // Calculate text position (centered)
        FontMetrics fm = g2d.getFontMetrics();
        int x = (getWidth() - fm.stringWidth(text)) / 2;
        int y = (getHeight() + fm.getHeight()) / 2;

        g2d.drawString(text, x, y);
    }

    public static void main(String[] args) {
        try {
            JFrame frame = new JFrame("Image Text Overlay");
            ImageTextOverlay panel = new ImageTextOverlay("path/to/image.jpg");
            frame.add(panel);
            frame.setSize(800, 600);
            frame.setDefaultCloseOperation(JFrame.EXIT_ON_CLOSE);
            frame.setVisible(true);
        } catch (IOException e) {
            System.out.println("Error loading image: " + e.getMessage());
        }
    }
