#include <iostream>
#include <vector>

const int WIDTH = 10;
const int HEIGHT = 10;

enum class Color {
    BLACK,
    WHITE,
};

class PixelArt {
private:
    std::vector<std::vector<Color>> pixels;

public:
    PixelArt() : pixels(HEIGHT, std::vector<Color>(WIDTH, Color::WHITE)) {}

    void draw(int x, int y) {
        if (x >= 0 && x < WIDTH && y >= 0 && y < HEIGHT)
            pixels[y][x] = Color::BLACK;
    }

    void erase(int x, int y) {
        if (x >= 0 && x < WIDTH && y >= 0 && y < HEIGHT)
            pixels[y][x] = Color::WHITE;
    }

    void fill(int x, int y, Color color) {
        if (x >= 0 && x < WIDTH && y >= 0 && y < HEIGHT) {
            std::vector<std::pair<int, int>> queue{{x, y}};
            while (!queue.empty()) {
                auto [cx, cy] = queue.back();
                queue.pop_back();
                if (pixels[cy][cx] != color) {
                    pixels[cy][cx] = color;
                    if (cx > 0) queue.emplace_back(cx - 1, cy);
                    if (cx < WIDTH - 1) queue.emplace_back(cx + 1, cy);
                    if (cy > 0) queue.emplace_back(cx, cy - 1);
                    if (cy < HEIGHT - 1) queue.emplace_back(cx, cy + 1);
                }
            }
        }
    }

    void save(const std::string& filename) {
        std::ofstream file(filename);
        for (const auto& row : pixels) {
            for (auto pixel : row)
                file << (pixel == Color::BLACK ? '*' : ' ');
            file << '\n';
        }
    }

    void print() const {
        for (const auto& row : pixels) {
            for (auto pixel : row)
                std::cout << (pixel == Color::BLACK ? '*' : ' ');
            std::cout << '\n';
        }
    }
};

int main() {
    PixelArt art;
    while (true) {
        std::cout << "Enter command: ";
        char command;
        int x, y;
        std::cin >> command;
        switch (command) {
            case 'd':
                std::cin >> x >> y;
                art.draw(x, y);
                break;
            case 'e':
                std::cin >> x >> y;
                art.erase(x, y);
                break;
            case 'f':
                std::cin >> x >> y;
                Color color = Color::BLACK; // For simplicity, always fill with black
                art.fill(x, y, color);
                break;
            case 's':
                std::string filename;
                std::cin >> filename;
                art.save(filename);
                return 0;
            case 'p':
                art.print();
                break;
            default:
                std::cout << "Unknown command" << std::endl;
        }
    }
    return 0;
}

#include <SFML/Graphics.hpp>
#include <vector>
#include <stack>

const int width = 800;
const int height = 600;
const int cellSize = 20;

struct Cell {
    bool visited;
    std::vector<int> neighbors; // Indices of neighboring cells
};

std::vector<Cell> maze(width * height);
int currentCell = 0;

void generateMaze() {
    while (true) {
        if (maze[currentCell].visited) {
            int lastVisited = currentCell;
            do {
                currentCell = maze[lastVisited].neighbors[rand() % maze[lastVisited].neighbors.size()];
            } while (maze[currentCell].visited);
        }

        maze[currentCell].visited = true;

        // Add unvisited neighbors to the stack
        for (int i = 0; i < 4; ++i) {
            int x = currentCell % width + (i % 2 ? 1 : -1);
            int y = currentCell / width + (i >= 2 ? 1 : -1);
            if (x >= 0 && x < width && y >= 0 && y < height && !maze[y * width + x].visited) {
                maze[currentCell].neighbors.push_back(y * width + x);
            }
        }

        if (maze[currentCell].neighbors.empty()) {
            // Backtrack
            int lastVisited = currentCell;
            do {
                currentCell = maze[lastVisited].neighbors[rand() % maze[lastVisited].neighbors.size()];
            } while (maze[currentCell].visited);
        }

        if (currentCell == 0) break; // Start from the top-left corner
    }
}

int main() {
    sf::RenderWindow window(sf::VideoMode(width, height), "Maze Solver");

    generateMaze();

    while (window.isOpen()) {
        sf::Event event;
        while (window.pollEvent(event)) {
            if (event.type == sf::Event::Closed)
                window.close();
        }

        window.clear();

        for (int i = 0; i < height; ++i) {
            for (int j = 0; j < width; ++j) {
                sf::RectangleShape rect(sf::Vector2f(cellSize, cellSize));
                rect.setPosition(j * cellSize, i * cellSize);

                if (maze[i * width + j].visited) {
                    rect.setFillColor(sf::Color(255, 0, 0));
                } else {
                    rect.setFillColor(sf::Color(0, 0, 255));
                }

                window.draw(rect);
            }
        }

        window.display();
    }

    return 0;
}

#include <iostream>
#include <fstream>
#include <string>
#include <sstream>
#include <unordered_map>

// Function to tokenize code into meaningful units
std::vector<std::string> tokenize(const std::string& code) {
    std::istringstream iss(code);
    std::vector<std::string> tokens;
    std::string token;
    while (iss >> token) {
        tokens.push_back(token);
    }
    return tokens;
}

// Function to compare two files for similarity
double compareFiles(std::ifstream& file1, std::ifstream& file2) {
    std::vector<std::string> tokens1 = tokenize((std::istreambuf_iterator<char>(file1)) , std::istreambuf_iterator<char>());
    std::vector<std::string> tokens2 = tokenize((std::istreambuf_iterator<char>(file2)) , std::istreambuf_iterator<char>());

    // Count common tokens
    std::unordered_map<std::string, int> tokenCount;
    for (const auto& token : tokens1) {
        ++tokenCount[token];
    }

    double commonTokens = 0;
    for (const auto& token : tokens2) {
        if (tokenCount[token] > 0) {
            ++commonTokens;
            --tokenCount[token];
        }
    }

    // Calculate similarity
    return commonTokens / (tokens1.size() + tokens2.size());
}

int main(int argc, char* argv[]) {
    if (argc != 3) {
        std::cerr << "Usage: " << argv[0] << " <file1> <file2>" << std::endl;
        return 1;
    }

    std::ifstream file1(argv[1]);
    std::ifstream file2(argv[2]);

    if (!file1.is_open() || !file2.is_open()) {
        std::cerr << "Error: Unable to open one or both of the files." << std::endl;
        return 1;
    }

    double similarity = compareFiles(file1, file2);
    std::cout << "Similarity between files: " << (similarity * 100) << "%" << std::endl;

    return 0;
}

using System;
using System.Drawing;

public class ImageProcessor
{
    public static Bitmap ResizeImage(Bitmap originalImage, int newWidth, int newHeight)
    {
        return new Bitmap(originalImage, newWidth, newHeight);
    }

    public static Bitmap CropImage(Bitmap originalImage, Rectangle cropArea)
    {
        return originalImage.Clone(cropArea, originalImage.PixelFormat);
    }

    public static void ApplyFilter(Bitmap image, Filter filter)
    {
        for (int y = 0; y < image.Height; y++)
        {
            for (int x = 0; x < image.Width; x++)
            {
                Color pixelColor = image.GetPixel(x, y);
                Color newColor = filter.Transform(pixelColor);
                image.SetPixel(x, y, newColor);
            }
        }
    }
}

public delegate Color Filter(Color color);

// Example usage
class Program
{
    static void Main()
    {
        Bitmap originalImage = new Bitmap("path_to_image.jpg");
        Bitmap resizedImage = ImageProcessor.ResizeImage(originalImage, 300, 200);
        Bitmap croppedImage = ImageProcessor.CropImage(resizedImage, new Rectangle(50, 50, 100, 100));

        // Applying a simple filter (e.g., grayscale)
        Filter grayScaleFilter = c => Color.FromArgb((c.R + c.G + c.B) / 3, (c.R + c.G + c.B) / 3, (c.R + c.G + c.B) / 3);
        ImageProcessor.ApplyFilter(croppedImage, grayScaleFilter);

        croppedImage.Save("processed_image.jpg");
    }
}

#include <iostream>
#include <vector>
#include <algorithm>
#include <random>

const int BOARD_SIZE = 16;
const int MAX_MATCHES = BOARD_SIZE / 2;

struct Tile {
    char content;
    bool visible;
};

class MemoryGame {
public:
    MemoryGame() : board(BOARD_SIZE), matchedCount(0) {
        initializeBoard();
        shuffleBoard();
    }

    void play() {
        while (matchedCount < MAX_MATCHES) {
            showBoard();
            int firstChoice = getTileIndex("Choose the first tile to flip (1-16): ");
            int secondChoice = getTileIndex("Choose the second tile to flip (1-16): ");

            if (firstChoice == secondChoice || board[firstChoice].visible || board[secondChoice].visible) {
                std::cout << "Invalid choice. Try again.\n";
                continue;
            }

            board[firstChoice].visible = true;
            board[secondChoice].visible = true;

            showBoard();

            if (board[firstChoice].content == board[secondChoice].content) {
                matchedCount++;
                std::cout << "Match! You found a pair!\n";
            } else {
                std::this_thread::sleep_for(std::chrono::seconds(1));
                board[firstChoice].visible = false;
                board[secondChoice].visible = false;
                std::cout << "No match. Try again.\n";
            }
        }

        std::cout << "Congratulations! You found all pairs!\n";
    }

private:
    void initializeBoard() {
        char characters[] = {'A', 'B', 'C', 'D'};
        for (int i = 0; i < MAX_MATCHES; ++i) {
            board[i * 2] = {characters[i % 4], false};
            board[i * 2 + 1] = {characters[i % 4], false};
        }
    }

    void shuffleBoard() {
        std::random_device rd;
        std::mt19937 g(rd());
        std::shuffle(board.begin(), board.end(), g);
    }

    int getTileIndex(const std::string& prompt) {
        int index;
        std::cout << prompt;
        std::cin >> index;
        return --index; // Convert to zero-based index
    }

    void showBoard() const {
        for (int i = 0; i < BOARD_SIZE; ++i) {
            if (board[i].visible) {
                std::cout << board[i].content << " ";
            } else {
                std::cout << "* ";
            }
            if ((i + 1) % 4 == 0) {
                std::cout << "\n";
            }
        }
    }

private:
    Tile board[BOARD_SIZE];
    int matchedCount;
};

int main() {
    MemoryGame game;
    game.play();
    return 0;
}

