using namespace cv;
using namespace std;

void recognizeText(const string& imagePath) {
    // Load image
    Mat img = imread(imagePath);
    if (img.empty()) {
        cout << "Could not open or find the image" << endl;
        return;
    }

    // Create a Tesseract instance
    tesseract::TesseractOCR api;

    // Initialize API with English language
    api.Init(NULL, "eng", tesseract::OEM_DEFAULT);

    // Convert image to grayscale
    Mat grayImg;
    cvtColor(img, grayImg, COLOR_BGR2GRAY);

    // Perform OCR
    string text = api.DoOCR(grayImg.data, grayImg.cols * grayImg.rows, tesseract::PSM_AUTO_OSD);

    // Output recognized text
    cout << "Recognized Text: " << text << endl;

    // Clean up
    api.End();
}

int main(int argc, char** argv) {
    if (argc != 2) {
        cout << "Usage: " << argv[0] << " <image_path>" << endl;
        return -1;
    }

    recognizeText(argv[1]);

    return 0;
}

