// Initialize the GLUT library and create a new window
void init() {
    glutInitDisplayMode(GLUT_DOUBLE | GLUT_RGB);
    glutInitWindowSize(windowWidth, windowHeight);
    glutCreateWindow("Interactive 3D Maze Game");
