// Draw the maze
void drawMaze() {
    glBegin(GL_QUADS);
    for (float x = -mazeSize / 2; x <= mazeSize / 2; x += 0.5f) {
        glVertex3f(x, mazeSize / 2, -mazeSize / 2);
        glVertex3f(x, mazeSize / 2, mazeSize / 2);
        glVertex3f(x + 0.5f, mazeSize / 2, mazeSize / 2);
        glVertex3f(x + 0.5f, mazeSize / 2, -mazeSize / 2);
    }
    glEnd();
}

// Draw the player
void drawPlayer() {
    glColor3f(1.0f, 0.0f, 0.0f); // Red color for the player
    glTranslatef(playerX, playerY, playerZ);
    glutSolidSphere(0.2f, 32, 32); // Draw a sphere as the player
}

// Display function to render the scene
void display() {
    glClear(GL_COLOR_BUFFER_BIT | GL_DEPTH_BUFFER_BIT);
    glLoadIdentity();
    gluPerspective(45.0f, (GLfloat)windowWidth / windowHeight, 1.0f, 100.0f);
    gluLookAt(playerX, playerY + 2.0f, playerZ + 2.0f, playerX, playerY, playerZ, 0.0f, 1.0f, 0.0f);

    drawMaze();
    drawPlayer();

    glutSwapBuffers();
}

// Keyboard function to move the player
void keyboard(unsigned char key, int x, int y) {
    switch (key) {
        case 'w': // Move forward
            playerZ -= 0.1f;
            break;
        case 's': // Move backward
            playerZ += 0.1f;
            break;
        case 'a': // Move left
            playerX -= 0.1f;
            break;
        case 'd': // Move right
            playerX += 0.1f;
            break;
    }
    glutPostRedisplay();
}

// Main function
int main(int argc, char** argv) {
    glutInit(&argc, argv);
    init();
    glutDisplayFunc(display);
    glutKeyboardFunc(keyboard);
    glEnable(GL_DEPTH_TEST);
    glutMainLoop();
    return 0;
}

