use piston::window::WindowSettings;
use piston::event_loop::{EventLoop, EventLoopBuilder};
use piston::input::*;
use opengl_graphics::{GlGraphics, G2d};

const WIDTH: u32 = 800;
const HEIGHT: u32 = 600;

fn main() {
    let mut window_settings = WindowSettings::new("Pixel Painter", [WIDTH, HEIGHT])
        .resizable(true)
        .exit_on_esc(true);

    let mut window = window_settings.build().unwrap();
    let mut events = EventLoopBuilder::new().build_windowed(window).expect("Window creation failed");
    let opengl: OpenGL = window.gl_version();
    let gl = GlGraphics::new(opengl);

    let mut pixels: Vec<u32> = vec![0; (WIDTH * HEIGHT) as usize];
    let mut current_color = 0xFFFFFFFF; // White

    while events.next() {
        if let Some(e) = e.update_args {
            // Handle updates
        }

        if let Some(e) = e.render_args {
            window.draw_2d(&e, |c, g| {
                clear([1.0, 1.0, 1.0, 1.0], g);
                for (i, &pixel) in pixels.iter().enumerate() {
                    let x = i % WIDTH as usize;
                    let y = i / WIDTH as usize;
                    rectangle(pixel as [f32; 4], [x as f64 * 8.0, y as f64 * 8.0, 8.0, 8.0], c.transform, g);
                }
            });
        }

        if let Some(e) = e.mouse_input {
            match e.press_and_release() {
                (Some(Button::Mouse(button)), Position(x, y), _) => {
                    if button == Button::Left && x >= 0.0 && y >= 0.0 && x < WIDTH as f64 && y < HEIGHT as f64 {
                        let i = ((y / 8.0) * WIDTH as f64 + (x / 8.0)) as usize;
                        pixels[i] = current_color;
                    }
                },
                _ => (),
            }
        }

        if let Some(e) = e.press_and_release() {
            match e.press_and_release() {
                (Some(Button::Keyboard(key)), _, _) => {
                    match key {
                        Key::R => current_color = 0xFFFF0000, // Red
                        Key::G => current_color = 0xFF00FF00, // Green
                        Key::B => current_color = 0x0000FFFF, // Blue
                        _ => (),
                    }
                },
                _ => (),
            }
        }
    }
}

using System;
using System.Drawing;
using System.Windows.Forms;

public class PixelArtEditor : Form
{
    private Bitmap bitmap;
    private Graphics graphics;
    private Point lastPoint = new Point(-1, -1);
    private bool drawing = false;
    private Color currentColor = Color.Black;

    public PixelArtEditor(int width, int height)
    {
        bitmap = new Bitmap(width, height);
        graphics = Graphics.FromImage(bitmap);
        this.Width = width;
        this.Height = height;
        this.Paint += new PaintEventHandler(PixelArtEditor_Paint);
        this.MouseDown += new MouseEventHandler(PixelArtEditor_MouseDown);
        this.MouseMove += new MouseEventHandler(PixelArtEditor_MouseMove);
        this.MouseUp += new MouseEventHandler(PixelArtEditor_MouseUp);
    }

    private void PixelArtEditor_Paint(object sender, PaintEventArgs e)
    {
        e.Graphics.DrawImage(bitmap, 0, 0);
    }

    private void PixelArtEditor_MouseDown(object sender, MouseEventArgs e)
    {
        lastPoint = e.Location;
        drawing = true;
    }

    private void PixelArtEditor_MouseMove(object sender, MouseEventArgs e)
    {
        if (drawing && (e.X >= 0) && (e.Y >= 0))
        {
            graphics.FillRectangle(new SolidBrush(currentColor), new Rectangle(e.X - 1, e.Y - 1, 2, 2));
            this.Invalidate();
            lastPoint = e.Location;
        }
    }

    private void PixelArtEditor_MouseUp(object sender, MouseEventArgs e)
    {
        drawing = false;
    }

    [STAThread]
    static void Main()
    {
        Application.EnableVisualStyles();
        Application.SetCompatibleTextRenderingDefault(false);
        Application.Run(new PixelArtEditor(400, 300));
    }
}

