// Function to convert note to frequency in Hz
double noteToFrequency(const std::string& note) {
    static const std::unordered_map<std::string, double> noteFrequencies = {
        {"A", 440.0}, {"Bb", 466.16}, {"B", 493.88},
        {"C", 523.25}, {"Db", 554.37}, {"D", 587.33},
        {"Eb", 622.25}, {"E", 659.25}, {"F", 698.46},
        {"F#", 739.99}, {"G", 783.99}, {"Ab", 830.61}
    };

    // Handle sharp and flat symbols
    std::string baseNote = note.substr(0, note.length() - 1);
    char alteration = note.back();
    int semitoneOffset = (alteration == '#') ? 1 : (alteration == 'b' ? -1 : 0);

    return noteFrequencies.at(baseNote) * std::pow(2.0, static_cast<double>(semitoneOffset) / 12);
}

int main() {
    std::ifstream inputFile("music_notes.txt");
    if (!inputFile.is_open()) {
        std::cerr << "Failed to open file!" << std::endl;
        return 1;
    }

    std::string note;
    while (getline(inputFile, note)) {
        double frequency = noteToFrequency(note);
        std::cout << "Note: " << note << ", Frequency: " << frequency << " Hz" << std::endl;
    }

    inputFile.close();
    return 0;
}

using System;
using Microsoft.ML;
using Microsoft.ML.Data;

public class LanguageModel
{
    private MLContext _context;
    private ITransformer _model;

    public LanguageModel()
    {
        _context = new MLContext();
    }

    public void Train(string[] trainingData)
    {
        // Define the data schema
        var dataView = _context.Data.LoadFromEnumerable(trainingData);

        // Create a pipeline for text processing and model training
        var pipeline = _context.Transforms.Text.FeaturizeText("Features", nameof(TextData))
            .Append(_context.MulticlassClassification.Trainers.SdcaNonCalibrated())
            .Append(_context.Transforms.Conversion.MapValueToKey("PredictedLabel"));

        // Train the model
        _model = pipeline.Fit(dataView);
    }

    public string Predict(string inputText)
    {
        var predictionEngine = _context.Model.CreatePredictionEngine<TextData, Prediction>(this._model);

        var result = predictionEngine.Predict(new TextData { Text = inputText });

        return result.PredictedLabel;
    }
}

