using System;
using System.Drawing;

public class PixelArtConverter
{
    public static Bitmap ConvertToPixelArt(Bitmap inputImage, int pixelSize)
    {
        if (inputImage == null || pixelSize <= 0)
        {
            throw new ArgumentException("Invalid input parameters");
        }

        int width = inputImage.Width;
        int height = inputImage.Height;

        // Create a new bitmap with the dimensions of the pixel art
        Bitmap pixelArt = new Bitmap(width / pixelSize, height / pixelSize);

        // Iterate through each pixel in the original image and set the corresponding pixel in the new image
        for (int y = 0; y < height; y += pixelSize)
        {
            for (int x = 0; x < width; x += pixelSize)
            {
                Color averageColor = GetAverageColor(inputImage, x, y, pixelSize);
                pixelArt.SetPixel(x / pixelSize, y / pixelSize, averageColor);
            }
        }

        return pixelArt;
    }

    private static Color GetAverageColor(Bitmap image, int x, int y, int size)
    {
        // Calculate the average color of a block of pixels
        int totalRed = 0;
        int totalGreen = 0;
        int totalBlue = 0;

        for (int i = 0; i < size; i++)
        {
            for (int j = 0; j < size; j++)
            {
                Color pixelColor = image.GetPixel(x + i, y + j);
                totalRed += pixelColor.R;
                totalGreen += pixelColor.G;
                totalBlue += pixelColor.B;
            }
        }

        int count = size * size;
        return Color.FromArgb(totalRed / count, totalGreen / count, totalBlue / count);
    }
}

