use reqwest::Error;
use serde::{Deserialize, Serialize};
use std::env;

#[derive(Serialize)]
struct WeatherRequest {
    q: String,
    appid: String,
}

#[derive(Deserialize)]
struct CurrentWeather {
    weather: Vec<Weather>,
    main: Main,
}

#[derive(Deserialize)]
struct Weather {
    description: String,
}

#[derive(Deserialize)]
struct Main {
    temp: f64,
    humidity: u32,
}

async fn fetch_weather(city: &str) -> Result<(), Error> {
    let api_key = env::var("WEATHER_API_KEY").expect("Missing WEATHER_API_KEY environment variable");
    let request_body = WeatherRequest { q: city.to_string(), appid: api_key };
    let client = reqwest::Client::new();
    let response = client.post("http://api.openweathermap.org/data/2.5/weather")
        .json(&request_body)
        .send()
        .await?
        .json::<CurrentWeather>()
        .await?;

    println!("{}: {}°C, {}", city, response.main.temp - 273.15, response.weather[0].description);
    println!("Humidity: {}%", response.main.humidity);

    Ok(())
}

#[tokio::main]
async fn main() -> Result<(), Error> {
    let city = env::args().nth(1).expect("Please provide a city name as an argument.");
    fetch_weather(&city).await?;
    Ok(())
}

import java.util.*;

public class MusicRecommender {
    private Map<String, Set<String>> userPreferences;
    private Map<String, Set<String>> songGenres;

    public MusicRecommender() {
        userPreferences = new HashMap<>();
        songGenres = new HashMap<>();
    }

    public void addUserPreference(String user, String song) {
        userPreferences.computeIfAbsent(user, k -> new HashSet<>()).add(song);
    }

    public void addSongGenre(String song, String genre) {
        songGenres.computeIfAbsent(genre, k -> new HashSet<>()).add(song);
    }

    public List<String> recommendSongsForUser(String user) {
        Set<String> preferredSongs = userPreferences.get(user);
        if (preferredSongs == null || preferredSongs.isEmpty()) {
            return Collections.emptyList();
        }

        Set<String> recommendedSongs = new HashSet<>();
        for (String song : preferredSongs) {
            String genre = getSongGenre(song);
            if (genre != null) {
                recommendedSongs.addAll(songGenres.get(genre));
                recommendedSongs.remove(song); // Avoid recommending the same song
            }
        }

        return new ArrayList<>(recommendedSongs);
    }

    private String getSongGenre(String song) {
        for (Map.Entry<String, Set<String>> entry : songGenres.entrySet()) {
            if (entry.getValue().contains(song)) {
                return entry.getKey();
            }
        }
        return null;
    }

    public static void main(String[] args) {
        MusicRecommender recommender = new MusicRecommender();

        // Adding user preferences and song genres
        recommender.addUserPreference("user1", "songA");
        recommender.addUserPreference("user1", "songB");
        recommender.addSongGenre("songA", "Pop");
        recommender.addSongGenre("songB", "Rock");
        recommender.addSongGenre("songC", "Pop");

        // Recommending songs for a user
        List<String> recommendations = recommender.recommendSongsForUser("user1");
        System.out.println("Recommended Songs: " + recommendations);
    }
}

