class Game {
    private Room currentRoom;

    public Game() {
        // Create rooms
        Room livingRoom = new Room("You are in a cozy living room. There is a door to the north and east.");
        Room kitchen = new Room("You are in the kitchen. There is a door to the south and west.");

        // Add exits between rooms
        livingRoom.AddExit("north", kitchen);
        livingRoom.AddExit("east", null);
        kitchen.AddExit("south", livingRoom);
        kitchen.AddExit("west", null);

        currentRoom = livingRoom;
    }

    public void Play() {
        while (true) {
            Console.WriteLine(currentRoom.Description);
            string command = Console.ReadLine().ToLower();

            if (command == "exit") break;

            switch (command) {
                case "look north":
                case "north":
                    currentRoom = currentRoom.GetExit("north");
                    break;
                case "look south":
                case "south":
                    currentRoom = currentRoom.GetExit("south");
                    break;
                case "look east":
                case "east":
                    currentRoom = currentRoom.GetExit("east");
                    break;
                case "look west":
                case "west":
                    currentRoom = currentRoom.GetExit("west");
                    break;
                default:
                    Console.WriteLine("I don't understand that command.");
                    break;
            }
        }

        Console.WriteLine("Thank you for playing!");
    }
}

