        Set<String> userPrefSet = new HashSet<>(userPreferences.get(username));
        List<String> recommendations = new ArrayList<>();

        for (String song : allSongs) {
            if (!userPrefSet.contains(song)) {
                recommendations.add(song);
            }
        }

        Collections.shuffle(recommendations);
        return recommendations;
    }

    public static void main(String[] args) {
        MusicRecommendationSystem system = new MusicRecommendationSystem();
        system.addUserPreference("Alice", Arrays.asList("Song1", "Song2"));
        List<String> recommendedSongs = system.recommendSongs("Alice");
        System.out.println("Recommended songs for Alice: " + recommendedSongs);
    }
}

using System;
using System.Collections.Generic;
using System.Drawing;
using System.Linq;

class ColorPaletteGenerator
{
    static void Main(string[] args)
    {
        if (args.Length == 0)
        {
            Console.WriteLine("Please provide an image file path as a parameter.");
            return;
        }

        string imagePath = args[0];
        using (Bitmap bitmap = new Bitmap(imagePath))
        {
            List<Color> palette = GenerateColorPalette(bitmap, 5);
            foreach (Color color in palette)
            {
                Console.WriteLine($"{color.R}, {color.G}, {color.B}");
            }
        }
    }

    static List<Color> GenerateColorPalette(Bitmap bitmap, int colorCount)
    {
        Dictionary<int[], int> colorHistogram = new Dictionary<int[], int>();

        for (int y = 0; y < bitmap.Height; y++)
        {
            for (int x = 0; x < bitmap.Width; x++)
            {
                Color pixelColor = bitmap.GetPixel(x, y);
                int[] key = new int[] { pixelColor.R, pixelColor.G, pixelColor.B };
                if (!colorHistogram.ContainsKey(key))
                {
                    colorHistogram[key] = 1;
                }
                else
                {
                    colorHistogram[key]++;
                }
            }
        }

        var sortedColors = colorHistogram.OrderByDescending(pair => pair.Value).Take(colorCount);
        return sortedColors.Select(pair => Color.FromArgb(pair.Key[0], pair.Key[1], pair.Key[2])).ToList();
    }
}

