#include <opencv2/opencv.hpp>
#include <vector>

using namespace cv;
using namespace std;

void stitchImages(const vector<Mat>& images, Mat& output) {
    Ptr<StereoSGBM> stereo = StereoSGBM::create(0, 16, 3);
    Ptr<StereoBM> bm = StereoBM::create(16, 5);

    // Detect keypoints and descriptors
    vector<KeyPoint> keypoints;
    Mat descriptors;
    Ptr<Feature2D> orb = ORB::create();
    for (const auto& img : images) {
        orb->detectAndCompute(img, noArray(), keypoints, descriptors);
    }

    // Match keypoints between images
    vector<vector<DMatch>> matches(images.size() - 1, vector<DMatch>());
    Ptr<BFMatcher> bf = BFMatcher(NORM_HAMMING, true);
    for (size_t i = 0; i < images.size(); ++i) {
        for (size_t j = i + 1; j < images.size(); ++j) {
            bf->match(descriptors.rowRange(i * keypoints[i].size(), (i + 1) * keypoints[i].size()),
                      descriptors.rowRange(j * keypoints[j].size(), (j + 1) * keypoints[j].size()),
                      matches[i - j]);
        }
    }

    // Stitch images
    Ptr<Stitcher> stitcher = Stitcher::create();
    stitcher->stitch(images, output);
}

int main() {
    vector<Mat> images = {imread("image1.jpg"), imread("image2.jpg")};
    Mat panorama;
    stitchImages(images, panorama);
    imshow("Panorama", panorama);
    waitKey(0);
    return 0;
}

