use std::fs;
use std::io::{self, Write};
use image::{DynamicImage, GenericImageView, ImageFormat};

enum FilterType {
    Grayscale,
    Sepia,
    Blur(u32),
}

fn apply_filter(input: &str, output: &str, filter_type: FilterType) -> io::Result<()> {
    let img = image::open(input)?;
    let (width, height) = img.dimensions();

    let filtered_img = match filter_type {
        FilterType::Grayscale => img.to_luma8(),
        FilterType::Sepia => {
            img.pixels()
                .map(|(_, _, &p)| {
                    let luma = p.luminance();
                    image::Rgba([
                        (0.393 * f32::from(luma) + 0.769 * f32::from(p.green()) + 0.189 * f32::from(p.blue())) as u8,
                        (0.349 * f32::from(luma) + 0.686 * f32::from(p.green()) + 0.168 * f32::from(p.blue())) as u8,
                        (0.272 * f32::from(luma) + 0.534 * f32::from(p.green()) + 0.131 * f32::from(p.blue())) as u8,
                        p.alpha(),
                    ])
                })
                .collect()
        },
        FilterType::Blur(radius) => img.resize_to_fit(width / radius, height / radius).resize_exact(width, height, image::imageops::FilterType::Lanczos3),
    };

    let mut out_file = fs::File::create(output)?;
    filtered_img.write_to(&mut out_file, ImageFormat::Png)?;

    Ok(())
}

fn main() -> io::Result<()> {
    if std::env::args().len() < 4 {
        eprintln!("Usage: image_filter <input> <output> <filter>");
        return Err(io::Error::new(io::ErrorKind::InvalidInput, "Not enough arguments provided"));
    }

    let input = &std::env::args().nth(1).unwrap();
    let output = &std::env::args().nth(2).unwrap();
    let filter_str = &std::env::args().nth(3).unwrap();

    let filter_type = match filter_str {
        "grayscale" => FilterType::Grayscale,
        "sepia" => FilterType::Sepia,
        "blur" => FilterType::Blur(5),
        _ => {
            eprintln!("Unknown filter type: {}", filter_str);
            return Err(io::Error::new(io::ErrorKind::InvalidInput, "Invalid filter type"));
        }
    };

    apply_filter(input, output, filter_type)?;

    println!("Filtered image saved to {}", output);

    Ok(())
}

