use image::{DynamicImage, GenericImageView};
use std::sync::mpsc;
use rayon::prelude::*;

fn apply_filter(image: DynamicImage) -> DynamicImage {
    let (tx, rx) = mpsc::channel();

    // Split the image into chunks for parallel processing
    let parts: Vec<_> = image.pixels().collect();

    // Process each chunk in a separate thread
    let filtered_parts = parts.par_iter().map(|&(_, _, pixel)| {
        // Apply a simple filter (e.g., brightness adjustment)
        pixel.map_lightness(|lightness| lightness * 1.2)
    }).collect();

    // Combine the processed chunks back into an image
    DynamicImage::from_pixels(image.width(), image.height(), filtered_parts.to_vec())
}

