func main() {
	// Load an example image (you can replace this with any image file path)
	imgFile := "path/to/your/image.jpg"
	img, err := loadImage(imgFile)
	if err != nil {
		fmt.Println("Error loading image:", err)
		return
	}

	// Generate color palette from the image
	colorPalette := generateColorPalette(img)

	// Print out the palette
	for i, c := range colorPalette {
		fmt.Printf("Color %d: R=%d G=%d B=%d\n", i+1, c.R, c.G, c.B)
	}
}

func loadImage(filePath string) (image.Image, error) {
	imgFile, err := os.Open(filePath)
	if err != nil {
		return nil, err
	}
	defer imgFile.Close()

	img, _, err := image.Decode(imgFile)
	if err != nil {
		return nil, err
	}

	return img, nil
}

func generateColorPalette(img image.Image) []color.RGBA {
	bounds := img.Bounds()
	totalPixels := (bounds.Dx() * bounds.Dy())
	colorMap := make(map[color.RGBA]int)

	for y := bounds.Min.Y; y < bounds.Max.Y; y++ {
		for x := bounds.Min.X; x < bounds.Max.X; x++ {
			c := color.RGBAModel.Convert(img.At(x, y)).(color.RGBA)
			colorMap[c]++
		}
	}

	var palette []color.RGBA
	for c := range colorMap {
		palette = append(palette, c)
	}

	// Sort palette by frequency (simple bubble sort for demonstration purposes)
	for i := 0; i < len(palette); i++ {
		for j := 0; j < len(palette)-i-1; j++ {
			if colorMap[palette[j]] < colorMap[palette[j+1]] {
				palette[j], palette[j+1] = palette[j+1], palette[j]
			}
		}
	}

	return palette
}

