    private static async Task<Weather> GetWeather(string cityName)
    {
        var url = $"https://api.openweathermap.org/data/2.5/weather?q={cityName}&appid={ApiKey}&units=metric";
        HttpResponseMessage response = await client.GetAsync(url);
        if (response.IsSuccessStatusCode)
        {
            string content = await response.Content.ReadAsStringAsync();
            return JsonSerializer.Deserialize<Weather>(content);
        }
        else
        {
            return null;
        }
    }

    private static void DisplayWeather(Weather weather)
    {
        Console.WriteLine($"City: {weather.Name}");
        Console.WriteLine($"Temperature: {weather.Main.Temp}°C");
        Console.WriteLine($"Humidity: {weather.Main.Humidity}%");
        Console.WriteLine($"Weather: {weather.Weather[0].Description}");
