using System;
using System.IO;
using Microsoft.WindowsAzure.Storage;
using Microsoft.WindowsAzure.Storage.Blob;

namespace AutoSyncer
{
    class Program
    {
        static void Main(string[] args)
        {
            string sourcePath = @"C:\SourceDirectory"; // Local directory to monitor
            string connectionString = "your_azure_storage_connection_string";
            string containerName = "sync-container";

            CloudStorageAccount storageAccount = CloudStorageAccount.Parse(connectionString);
            CloudBlobClient blobClient = storageAccount.CreateCloudBlobClient();
            CloudBlobContainer container = blobClient.GetContainerReference(containerName);

            // Create the container if it does not exist
            container.CreateIfNotExistsAsync().Wait();

            FileSystemWatcher watcher = new FileSystemWatcher(sourcePath)
            {
                NotifyFilter = NotifyFilters.LastAccess | NotifyFilters.LastWrite
                               | NotifyFilters.FileName | NotifyFilters.DirectoryName,
                IncludeSubdirectories = true
            };

            watcher.Changed += (sender, e) => SyncFile(e.FullPath, container);
            watcher.Created += (sender, e) => SyncFile(e.FullPath, container);

            Console.WriteLine("Press 'q' to quit the sample.");
            while (Console.Read() != 'q') ;
        }

        static void SyncFile(string filePath, CloudBlobContainer container)
        {
            string blobName = Path.GetRelativePath(@"C:\SourceDirectory", filePath);
            CloudBlockBlob blockBlob = container.GetBlockBlobReference(blobName);

            // Upload the file to Azure Blob Storage
            using (FileStream fileStream = File.OpenRead(filePath))
            {
                blockBlob.UploadFromStreamAsync(fileStream).Wait();
            }

            Console.WriteLine($"File {filePath} has been synced.");
        }
    }
}

