#include <iostream>
#include <vector>

class PixelPainter {
public:
    PixelPainter(int width, int height) : canvas(width, std::vector<int>(height)) {}

    void setPixel(int x, int y, int color) {
        if (x >= 0 && x < width && y >= 0 && y < height) {
            canvas[x][y] = color;
        }
    }

    int getPixel(int x, int y) const {
        return (x >= 0 && x < width && y >= 0 && y < height) ? canvas[x][y] : -1; // Return -1 for out-of-bound pixels
    }

    void display() const {
        std::cout << "Canvas (" << width << "x" << height << "):" << std::endl;
        for (const auto& row : canvas) {
            for (int pixel : row) {
                std::cout << pixel << " ";
            }
            std::cout << std::endl;
        }
    }

private:
    int width, height;
    std::vector<std::vector<int>> canvas;
};

