import (
    "fmt"
    "log"

    "github.com/btcsuite/btcd/chaincfg"
    "github.com/btcsuite/btcjson"
    "github.com/btcsuite/btcwallet/rpcclient"
)

func main() {
    // Connect to local Bitcoin node
    config := &rpcclient.ConnConfig{
        Host:         "localhost:8332",
        User:         "user",
        Pass:         "password",
        HTTPPostMode: true,
        DisableTLS:   true,
    }

    client, err := rpcclient.New(config, nil)
    if err != nil {
        log.Fatalf("Failed to connect to Bitcoin node: %v", err)
    }
    defer client.Shutdown()

    // Fetch the latest block hash
    blockHash, err := client.GetBestBlockHash()
    if err != nil {
        log.Fatalf("Failed to get best block hash: %v", err)
    }

    fmt.Printf("Latest Block Hash: %s\n", blockHash.String())

    // Fetch transactions from the latest block
    blockHeader, err := client.GetBlockHeader(blockHash)
    if err != nil {
        log.Fatalf("Failed to get block header: %v", err)
    }
    blockHeight := int64(blockHeader.Height)

    txs, err := client.GetRawTransactionsForBlocks([]int64{blockHeight})
    if err != nil {
        log.Fatalf("Failed to get raw transactions for blocks: %v", err)
    }

    // Print each transaction
    fmt.Println("Transactions in latest block:")
    for _, tx := range txs[0] {
        txDetails, err := client.GetRawTransactionVerbose(tx)
        if err != nil {
            log.Fatalf("Failed to get raw transaction details: %v", err)
        }
        fmt.Printf("%s\n", txDetails.Txid)
    }
}

