import java.awt.image.BufferedImage;
import java.io.File;
import java.io.IOException;
import javax.imageio.ImageIO;

public class PixelArtGenerator {
    public static void main(String[] args) throws IOException {
        BufferedImage originalImage = ImageIO.read(new File("input.jpg"));
        int width = 20; // Desired pixel art width
        int height = (int)((double)originalImage.getHeight() / originalImage.getWidth() * width);
        BufferedImage pixelArt = new BufferedImage(width, height, BufferedImage.TYPE_INT_RGB);

        for (int y = 0; y < height; y++) {
            for (int x = 0; x < width; x++) {
                int color = originalImage.getRGB(x * originalImage.getWidth() / width, y * originalImage.getHeight() / height);
                pixelArt.setRGB(x, y, color);
            }
        }

        ImageIO.write(pixelArt, "png", new File("output.png"));
    }
}

