class Playlist {
    private List<Song> songs;
    private String name;

    public Playlist(String name) {
        this.songs = new ArrayList<>();
        this.name = name;
    }

    public void addSong(Song song) {
        songs.add(song);
    }

    public boolean removeSong(Song song) {
        return songs.remove(song);
    }

