#include <iostream>
#include <vector>
#include <cmath>
#include "opencv2/opencv.hpp"

using namespace cv;
using namespace std;

class ColorQuantizer {
private:
    vector<Vec3b> centroids;
    int clusters;

public:
    ColorQuantizer(int k) : clusters(k) {}

    void train(const Mat &image) {
        // Convert image to LAB color space for better color quantization
        Mat labImage;
        cvtColor(image, labImage, COLOR_BGR2LAB);

        // Create a vector of points from the LAB image
        vector<Point3f> samples;
        for (int y = 0; y < image.rows; ++y) {
            for (int x = 0; x < image.cols; ++x) {
                Vec3b pixel = labImage.at<Vec3b>(y, x);
                samples.push_back(Point3f(pixel[0], pixel[1], pixel[2]));
            }
        }

        // Apply K-means clustering to find color centroids
        Mat labels;
        termCriteria criteria(TermCriteria::EPS + TermCriteria::MAX_ITER, 100, 1.0);
        kmeans(samples, clusters, labels, criteria, 3, KMEANS_PP_CENTERS, centroids);

        // Convert centroids back to 8-bit values
        for (int i = 0; i < centroids.size(); ++i) {
            centroids[i] = Vec3b(clipMat(centroids[i][0]), clipMat(centroids[i][1]), clipMat(centroids[i][2]));
        }
    }

    Mat quantize(const Mat &image) {
        // Create an output image with the same size as input
        Mat output(image.size(), CV_8UC3);
        for (int y = 0; y < image.rows; ++y) {
            for (int x = 0; x < image.cols; ++x) {
                Vec3b pixel = image.at<Vec3b>(y, x);

                // Convert pixel to LAB
                float l, a, b;
                cvtColor(Mat(1, 1, CV_8UC3, &pixel), Mat(1, 1, CV_32FC3, &l, &a, &b), COLOR_BGR2LAB);

                // Find the closest centroid
                int minIndex = 0;
                float minDist = norm(Mat(1, 1, CV_32FC3, l, a, b) - Mat(1, 1, CV_32FC3, centroids[0][0], centroids[0][1], centroids[0][2]));
                for (int i = 1; i < clusters; ++i) {
                    float dist = norm(Mat(1, 1, CV_32FC3, l, a, b) - Mat(1, 1, CV_32FC3, centroids[i][0], centroids[i][1], centroids[i][2]));
                    if (dist < minDist) {
                        minIndex = i;
                        minDist = dist;
                    }
                }

                // Set the pixel in the output image to the closest centroid
                output.at<Vec3b>(y, x) = centroids[minIndex];
            }
        }
        return output;
    }

private:
    float clipMat(float value) {
        if (value < 0.0f)
            return 0.0f;
        if (value > 255.0f)
            return 255.0f;
        return value;
    }
};

int main() {
    Mat image = imread("path_to_image.jpg");
    if (image.empty()) {
        cout << "Could not open or find the image" << endl;
        return -1;
    }

    ColorQuantizer quantizer(32); // Reduce to 32 colors
    quantizer.train(image);
    Mat result = quantizer.quantize(image);

    imshow("Original", image);
    imshow("Quantized", result);
    waitKey(0);

    return 0;
}

