    public static void main(String[] args) throws IOException {
        File file = new File("path/to/image.jpg");
        String caption = generateCaption(file);
        System.out.println("Generated Caption: " + caption);
    }

    private static String generateCaption(File imageFile) throws IOException {
        OkHttpClient client = new OkHttpClient();

        RequestBody requestBody = new MultipartBody.Builder()
                .setType(MultipartBody.FORM)
                .addFormDataPart("file", imageFile.getName(),
                        RequestBody.create(MediaType.parse("image/jpeg"), imageFile))
                .build();

        Request request = new Request.Builder()
                .url("https://api.openai.com/v1/images/edits")
                .post(requestBody)
                .addHeader("Authorization", "Bearer " + API_KEY)
                .addHeader("Content-Type", "multipart/form-data")
                .build();

        try (Response response = client.newCall(request).execute()) {
            if (!response.isSuccessful()) throw new IOException("Unexpected code " + response);

            JSONObject jsonObject = new JSONObject(response.body().string());
            return jsonObject.getJSONArray("data").getJSONObject(0).getString("caption");
        }
    }
}

