type Track struct {
    Title   string
    Artist  string
    Album   string
    Genre   string
    Duration int // in seconds
}

var tracks = []Track{}

func addTrack(title, artist, album, genre string, duration int) {
    track := Track{Title: title, Artist: artist, Album: album, Genre: genre, Duration: duration}
    tracks = append(tracks, track)
    fmt.Println("Track added successfully!")
}

func listTracks() {
    if len(tracks) == 0 {
        fmt.Println("No tracks found.")
        return
    }
    for _, track := range tracks {
        fmt.Printf("Title: %s\nArtist: %s\nAlbum: %s\nGenre: %s\nDuration: %d seconds\n\n", track.Title, track.Artist, track.Album, track.Genre, track.Duration)
    }
}

func main() {
    if len(os.Args) < 2 {
        fmt.Println("Usage: music_manager add <title> <artist> <album> <genre> <duration>")
        fmt.Println("       music_manager list")
        return
    }

    switch os.Args[1] {
    case "add":
        if len(os.Args) != 6 {
            fmt.Println("Invalid arguments. Usage: music_manager add <title> <artist> <album> <genre> <duration>")
            return
        }
        title := os.Args[2]
        artist := os.Args[3]
        album := os.Args[4]
        genre := os.Args[5]
        duration, _ := strconv.Atoi(os.Args[6])
        addTrack(title, artist, album, genre, duration)
    case "list":
        listTracks()
    default:
        fmt.Println("Invalid command. Use music_manager help for available commands.")
    }
}

