type WeatherData struct {
	Main struct {
		Temp float64 `json:"temp"`
	} `json:"main"`
}

func getWeather(city string) (float64, error) {
	url := "http://api.openweathermap.org/data/2.5/weather?q=" + city + "&appid=your_api_key&units=metric"
	resp, err := http.Get(url)
	if err != nil {
		return 0, err
	}
	defer resp.Body.Close()

	body, err := ioutil.ReadAll(resp.Body)
	if err != nil {
		return 0, err
	}

	var data WeatherData
	err = json.Unmarshal(body, &data)
	if err != nil {
		return 0, err
	}

	return data.Main.Temp, nil
}

func main() {
	fmt.Println("Enter a city to check the weather:")
	var city string
	fmt.Scan(&city)

	temp, err := getWeather(city)
	if err != nil {
		fmt.Println("Error:", err)
		return
	}

	fmt.Printf("The temperature in %s is %.2f°C\n", city, temp)
}

