package main

import (
	"fmt"
	"sync"
)

type MusicQueue struct {
	queue []string
	mutex sync.Mutex
}

func NewMusicQueue() *MusicQueue {
	return &MusicQueue{queue: make([]string, 0)}
}

func (mq *MusicQueue) AddSong(song string) {
	mq.mutex.Lock()
	defer mq.mutex.Unlock()
	mq.queue = append(mq.queue, song)
	fmt.Println("Added to queue:", song)
}

func (mq *MusicQueue) PlayNext() {
	if len(mq.queue) == 0 {
		fmt.Println("No songs in the queue.")
		return
	}
	nextSong := mq.queue[0]
	mq.queue = mq.queue[1:]
	fmt.Println("Playing now:", nextSong)
}

func (mq *MusicQueue) ViewQueue() {
	mq.mutex.Lock()
	defer mq.mutex.Unlock()
	if len(mq.queue) == 0 {
		fmt.Println("The queue is empty.")
	} else {
		fmt.Println("Current Queue:")
		for _, song := range mq.queue {
			fmt.Println("-", song)
		}
	}
}

func main() {
	mq := NewMusicQueue()

	wg := sync.WaitGroup{}
	wg.Add(3)

	go func() {
		defer wg.Done()
		mq.AddSong("Imagine")
	}()

	go func() {
		defer wg.Done()
		mq.PlayNext()
	}()

	go func() {
		defer wg.Done()
		mq.ViewQueue()
	}()

	wg.Wait()
}

