using System.Drawing;
using System.Windows.Forms;

namespace ImageFilterApp
{
    public partial class Form1 : Form
    {
        private Bitmap originalImage;

        public Form1()
        {
            InitializeComponent();
        }

        private void btnUpload_Click(object sender, EventArgs e)
        {
            using (OpenFileDialog ofd = new OpenFileDialog())
            {
                if (ofd.ShowDialog() == DialogResult.OK)
                {
                    originalImage = new Bitmap(ofd.FileName);
                    pictureBox.Image = originalImage;
                }
            }
        }

        private void btnGrayscale_Click(object sender, EventArgs e)
        {
            ApplyFilter(originalImage => ConvertToGrayscale(originalImage));
        }

        private void btnSepia_Click(object sender, EventArgs e)
        {
            ApplyFilter(originalImage => ApplySepiaEffect(originalImage));
        }

        private void btnBlur_Click(object sender, EventArgs e)
        {
            ApplyFilter(originalImage => ApplyBlurEffect(originalImage));
        }

        private Bitmap ConvertToGrayscale(Bitmap image)
        {
            for (int i = 0; i < image.Width; i++)
            {
                for (int j = 0; j < image.Height; j++)
                {
                    Color originalColor = image.GetPixel(i, j);
                    int grayScaleValue = (int)(originalColor.R * 0.3 + originalColor.G * 0.59 + originalColor.B * 0.11);
                    Color newColor = Color.FromArgb(grayScaleValue, grayScaleValue, grayScaleValue);
                    image.SetPixel(i, j, newColor);
                }
            }
            return image;
        }

        private Bitmap ApplySepiaEffect(Bitmap image)
        {
            for (int i = 0; i < image.Width; i++)
            {
                for (int j = 0; j < image.Height; j++)
                {
                    Color originalColor = image.GetPixel(i, j);
                    int sepiaR = (int)(originalColor.R * 0.393 + originalColor.G * 0.769 + originalColor.B * 0.189);
                    int sepiaG = (int)(originalColor.R * 0.349 + originalColor.G * 0.686 + originalColor.B * 0.168);
                    int sepiaB = (int)(originalColor.R * 0.272 + originalColor.G * 0.534 + originalColor.B * 0.131);

                    Color newColor = Color.FromArgb(Math.Min(255, sepiaR), Math.Min(255, sepiaG), Math.Min(255, sepiaB));
                    image.SetPixel(i, j, newColor);
                }
            }
            return image;
        }

        private Bitmap ApplyBlurEffect(Bitmap image)
        {
            int kernelSize = 3;
            int[,] blurKernel = { { 1, 1, 1 },
                                  { 1, 2, 1 },
                                  { 1, 1, 1 } };
            Bitmap resultImage = new Bitmap(image.Width, image.Height);

            for (int i = kernelSize / 2; i < image.Width - kernelSize / 2; i++)
            {
                for (int j = kernelSize / 2; j < image.Height - kernelSize / 2; j++)
                {
                    Color sumColor = Color.FromArgb(0, 0, 0);
                    int count = 0;

                    for (int x = -kernelSize / 2; x <= kernelSize / 2; x++)
                    {
                        for (int y = -kernelSize / 2; y <= kernelSize / 2; y++)
                        {
                            Color pixelColor = image.GetPixel(i + x, j + y);
                            sumColor.R += pixelColor.R * blurKernel[x + kernelSize / 2, y + kernelSize / 2];
                            sumColor.G += pixelColor.G * blurKernel[x + kernelSize / 2, y + kernelSize / 2];
                            sumColor.B += pixelColor.B * blurKernel[x + kernelSize / 2, y + kernelSize / 2];
                            count++;
                        }
                    }

                    resultImage.SetPixel(i, j, Color.FromArgb(sumColor.R / count, sumColor.G / count, sumColor.B / count));
                }
            }

            return resultImage;
        }

        private void ApplyFilter(Func<Bitmap, Bitmap> filter)
        {
            pictureBox.Image = filter(originalImage);
        }
    }
}

use cpal::traits::{DeviceTrait, HostTrait};
use cpal::StreamError;
use rustfft::num_complex::Complex;
use rustfft::{num_traits::Zero, FftPlanner};

fn main() -> Result<(), StreamError> {
    let host = cpal::default_host();
    let device = host.default_input_device().unwrap();
    let config = device.default_input_config().unwrap();

    let fft_planner = FftPlanner::<f32>::new(false);
    let fft = fft_planner.plan_fft_forward(config.sample_rate.0 as usize);

    let mut buffer: Vec<f32> = vec![Zero::zero(); config.buffer_size()];

    let stream_id = device.build_input_stream(
        &config,
        move |data, _: &cpal::InputCallbackInfo| {
            for (i, sample) in data.iter().enumerate() {
                buffer[i % buffer.len()] = *sample;
            }
        },
        move |_err| {},
        cpal::StreamSettingsError::None,
    )?;

    stream_id.play()?;

    loop {
        let mut real_part: Vec<f32> = vec![0.0; buffer.len()];
        for (i, sample) in buffer.iter().enumerate() {
            real_part[i] = *sample;
        }
        let complex_vec: Vec<Complex<f32>> = real_part.into_iter().map(|x| Complex { re: x, im: 0.0 }).collect();
        fft.process(&mut complex_vec);

        // Process the frequency data and generate visualizations
    }

    Ok(())
}

