enum GameState {
    Exploring,
    TalkingToNPC,
    // Add more states as needed
}

struct Game {
    current_state: GameState,
    player_health: u32,
    inventory: Vec<String>,
    location_description: String,
}

impl Game {
    fn new() -> Game {
        Game {
            current_state: GameState::Exploring,
            player_health: 100,
            inventory: Vec::new(),
            location_description: "You find yourself in a dense forest. There are paths to the north and east.".to_string(),
        }
    }

    fn handle_input(&mut self, input: &str) {
        match self.current_state {
            GameState::Exploring => self.explore(input),
            GameState::TalkingToNPC => self.talk_to_npc(input),
            // Handle other states
        }
    }

    fn explore(&mut self, direction: &str) {
        match direction.to_lowercase().as_str() {
            "north" => self.location_description = "You move north and find a small village.".to_string(),
            "east" => self.location_description = "You venture east and discover a waterfall.".to_string(),
            _ => println!("You can't go that way."),
        }
    }

    fn talk_to_npc(&mut self, npc_response: &str) {
        match npc_response.to_lowercase().as_str() {
            "hello" => println!("NPC says: Greetings! What brings you here?"),
            "take sword" => {
                if self.location_description.contains("sword") {
                    self.inventory.push("sword".to_string());
                    println!("You took the sword!");
                } else {
                    println!("There's no sword here.");
                }
            }
            _ => println!("NPC doesn't understand."),
        }
    }

    fn run(&mut self) {
        println!("{}", self.location_description);
        loop {
            print!("> ");
            io::stdout().flush().unwrap();
            let mut input = String::new();
            io::stdin().read_line(&mut input).expect("Failed to read line");
            self.handle_input(input.trim());
        }
    }
}

fn main() {
    let mut game = Game::new();
    game.run();
}

