class Room {
    public string Name { get; set; }
    public List<string> Description { get; set; }
    public Dictionary<string, Room> Exits { get; set; }

    public Room(string name) {
        Name = name;
        Description = new List<string>();
        Exits = new Dictionary<string, Room>();
    }

    public void AddDescription(string desc) {
        Description.Add(desc);
    }

    public void AddExit(string direction, Room room) {
        Exits[direction] = room;
    }

    public void Display() {
        Console.WriteLine(string.Join("\n", Description));
        foreach (var exit in Exits.Keys) {
            Console.WriteLine($"There is an {exit} door.");
        }
    }
}

class Game {
    private Room currentRoom;

    public Game() {
        // Create rooms
        Room livingRoom = new Room("Living Room");
        livingRoom.AddDescription("You are in a cozy living room with a fireplace and comfortable furniture.");

        Room kitchen = new Room("Kitchen");
        kitchen.AddDescription("The kitchen is dimly lit with old utensils scattered around.");

        // Add exits between rooms
        livingRoom.AddExit("north", kitchen);
        kitchen.AddExit("south", livingRoom);

        // Set the starting room
        currentRoom = livingRoom;
    }

    public void Play() {
        bool playing = true;
        while (playing) {
            currentRoom.Display();
            Console.Write("Which direction do you want to go? ");
            string direction = Console.ReadLine().ToLower();

            if (currentRoom.Exits.ContainsKey(direction)) {
                currentRoom = currentRoom.Exits[direction];
            } else {
                Console.WriteLine("You can't go that way.");
            }

            Console.WriteLine();
        }
    }
}

class Program {
    static void Main(string[] args) {
        Game game = new Game();
        game.Play();
    }
}

