using namespace cv;
using namespace std;

// Function to detect keypoints and describe them using ORB
void detectAndDescribeKeyPoints(const Mat& image, vector<KeyPoint>& keypoints, Mat& descriptors) {
    Ptr<ORB> orb = ORB::create();
    orb->detectAndCompute(image, noArray(), keypoints, descriptors);
}

// Function to match key points between two images
vector<DMatch> matchKeyPoints(Mat& descriptors1, Mat& descriptors2) {
    BFMatcher matcher(NORM_HAMMING, true);
    vector<DMatch> matches;
    matcher.match(descriptors1, descriptors2, matches);
    return matches;
}

// Function to draw matches between two images
void drawMatches(const vector<KeyPoint>& keypoints1, const vector<KeyPoint>& keypoints2,
                 const vector<DMatch>& matches, Mat& outputImage) {
    drawMatches(image1, keypoints1, image2, keypoints2, matches, outputImage);
}

int main() {
    // Load input images
    Mat image1 = imread("image1.jpg", IMREAD_COLOR);
    Mat image2 = imread("image2.jpg", IMREAD_COLOR);

    vector<KeyPoint> keypoints1, keypoints2;
    Mat descriptors1, descriptors2;

    // Detect keypoints and describe them
    detectAndDescribeKeyPoints(image1, keypoints1, descriptors1);
    detectAndDescribeKeyPoints(image2, keypoints2, descriptors2);

    // Match key points between the two images
    vector<DMatch> matches = matchKeyPoints(descriptors1, descriptors2);

    // Draw the matches on a new image
    Mat outputImage;
    drawMatches(keypoints1, keypoints2, matches, outputImage);

    // Display the result
    imshow("Matches", outputImage);
    waitKey(0);

    return 0;
}

