package main

import (
	"fmt"
	"os/exec"
)

func main() {
	inputFile := "example.md" // Change this to your markdown file
	outputFile := "/tmp/example.html"

	// Convert Markdown to HTML
	cmd := exec.Command("pandoc", "-f", "markdown", "-t", "html", "-o", outputFile, inputFile)
	err := cmd.Run()
	if err != nil {
		fmt.Println("Error converting markdown to HTML:", err)
		return
	}

	// Open the HTML file in a web browser
	browserCmd := exec.Command("xdg-open", outputFile) // For Linux systems
	// Uncomment the line below for macOS or Windows
	// browserCmd = exec.Command("open", outputFile)
	err = browserCmd.Start()
	if err != nil {
		fmt.Println("Error opening file with browser:", err)
		return
	}

	fmt.Println("Markdown preview opened in your default web browser.")
}

